-- SBA 7(a) Beginner (7 modules)
('sba-7(a)-module-1-beginner', 'SBA 7(a) Program Fundamentals Quiz', 'quiz', 1, 80, true, 3, 15, '[
  {"question": "What is the maximum loan amount for an SBA 7(a) loan?", "type": "multiple-choice", "options": ["$1 million", "$2 million", "$5 million", "$10 million"], "correctAnswer": "$5 million", "explanation": "The SBA 7(a) program allows loans up to $5 million for eligible small businesses.", "points": 10, "difficulty": "easy"},
  {"question": "Which federal agency guarantees SBA 7(a) loans?", "type": "multiple-choice", "options": ["Federal Reserve", "FDIC", "Small Business Administration", "Treasury Department"], "correctAnswer": "Small Business Administration", "explanation": "The U.S. Small Business Administration provides the government guarantee on 7(a) loans.", "points": 10, "difficulty": "easy"},
  {"question": "What is the typical SBA guarantee percentage for loans up to $150,000?", "type": "multiple-choice", "options": ["50%", "65%", "75%", "85%"], "correctAnswer": "85%", "explanation": "SBA guarantees up to 85% for loans of $150,000 or less, and 75% for loans above $150,000.", "points": 15, "difficulty": "medium"},
  {"question": "Which business is NOT eligible for SBA 7(a) financing?", "type": "multiple-choice", "options": ["Restaurant franchise", "Real estate investment company", "Manufacturing firm", "Retail store"], "correctAnswer": "Real estate investment company", "explanation": "Passive real estate investment companies are ineligible. The business must be actively operated.", "points": 15, "difficulty": "medium"}
]'::jsonb),

('sba-7(a)-module-2-beginner', 'SBA 7(a) Product Structures Quiz', 'quiz', 1, 80, true, 3, 15, '[
