  {"question": "What minimum credit score is typically needed?", "type": "multiple-choice", "options": ["580", "620", "680", "720"], "correctAnswer": "680", "explanation": "Most SBA lenders require a minimum FICO of 680.", "points": 10, "difficulty": "easy"},
