  {"question": "Personal guarantees are required from owners with what minimum stake?", "type": "multiple-choice", "options": ["10%", "15%", "20%", "25%"], "correctAnswer": "20%", "explanation": "All owners with 20%+ ownership must provide unlimited personal guarantees.", "points": 15, "difficulty": "medium"}
