  {"question": "What minimum DSCR is typically required for SBA 7(a)?", "type": "multiple-choice", "options": ["0.8x", "1.0x", "1.15x", "1.5x"], "correctAnswer": "1.15x", "explanation": "Most lenders require 1.15x DSCR minimum.", "points": 10, "difficulty": "medium"},
