-- Invoice Factoring Beginner Module 1
('invoice-factoring-module-1-beginner', 'Invoice Factoring Fundamentals Quiz', 'quiz', 1, 80, true, 3, 15, '[
  {"question": "What is invoice factoring?", "type": "multiple-choice", "options": ["Equipment loan", "Selling receivables at a discount for cash", "Revenue-based credit line", "Bad debt insurance"], "correctAnswer": "Selling receivables at a discount for cash", "explanation": "Factoring = selling outstanding invoices to a factor for immediate cash.", "points": 10, "difficulty": "easy"},
