INSERT INTO course_assessments (module_id, title, assessment_type, questions, passing_score, time_limit_minutes, order_index, is_required) VALUES
('sba-7(a)-module-2-expert', 'Advanced SBA 7(a) Deal Structuring', 'quiz', '[{"question":"What is the maximum SBA 7(a) guarantee for loans over $150,000?","options":["85%","75%","50%","90%"],"correct":1,"explanation":"SBA guarantees 75% for loans exceeding $150,000."},{"question":"Which structure best mitigates risk for a multi-use property SBA 7(a) loan?","options":["Full-recourse with blanket lien","Partial guarantee with cross-collateralization","Interest-only with balloon","Unsecured with personal guarantee"],"correct":1,"explanation":"Cross-collateralization spreads risk across multiple assets."},{"question":"What debt service coverage ratio typically satisfies SBA 7(a) underwriting?","options":["0.8x","1.0x","1.25x","2.0x"],"correct":2,"explanation":"A 1.25x DSCR is the standard minimum for SBA 7(a) approval."},{"question":"How should a lender structure standby debt in an SBA 7(a) transaction?","options":["As senior secured debt","On full standby for 2+ years","As mezzanine with equity kicker","As revolving facility"],"correct":1,"explanation":"Standby debt must remain on full standby for at least 2 years per SBA rules."}]', 85, 20, 1, true),
('sba-7(a)-module-3-expert', 'Advanced SBA 7(a) Financial Analysis', 'quiz', '[{"question":"When analyzing global cash flow for SBA 7(a), which entities are included?","options":["Only the borrowing entity","Borrower plus 20%+ owners affiliates","All entities in the same industry","Only entities with shared collateral"],"correct":1,"explanation":"SBA requires global cash flow analysis of the borrower and all 20%+ owner affiliates."},{"question":"What adjusted EBITDA metric best predicts SBA 7(a) repayment capacity?","options":["Gross revenue","Net income after taxes","EBITDA minus owner compensation adjustments","Operating cash flow"],"correct":2,"explanation":"Adjusted EBITDA normalizing owner compensation provides the most accurate repayment picture."},{"question":"Which financial ratio is most critical for SBA 7(a) change-of-ownership deals?","options":["Current ratio","Price-to-earnings","Debt-to-equity post-acquisition","Inventory turnover"],"correct":2,"explanation":"Post-acquisition debt-to-equity determines if the business can sustain the acquisition debt."},{"question":"How should historical losses be treated in SBA 7(a) projections?","options":["Ignored if one-time","Added back with documentation","Averaged over 5 years","Doubled for conservatism"],"correct":1,"explanation":"Documented one-time losses can be added back with proper explanation and evidence."}]', 85, 20, 1, true),
('sba-7(a)-module-4-expert', 'SBA 7(a) Regulatory & Compliance Framework', 'quiz', '[{"question":"What is the primary regulatory document governing SBA 7(a) lending?","options":["Dodd-Frank Act","SBA SOP 50 10","Federal Reserve Regulation Z","OCC Lending Manual"],"correct":1,"explanation":"SBA SOP 50 10 is the Standard Operating Procedures governing all SBA 7(a) lending."},{"question":"When must a lender obtain an environmental review for SBA 7(a)?","options":["All loans over $100K","Only manufacturing loans","When real estate is collateral and environmental risk exists","Never required"],"correct":2,"explanation":"Environmental reviews are required when real estate collateral has potential contamination risk."},{"question":"What triggers an SBA 7(a) loan servicing transfer notification requirement?","options":["Any payment modification","Change of lender ownership","Borrower name change","Annual review"],"correct":1,"explanation":"Lender ownership changes require SBA notification and potential loan transfer procedures."},{"question":"How often must SBA Preferred Lenders undergo program review?","options":["Annually","Every 2 years","Every 3 years","Every 5 years"],"correct":1,"explanation":"PLP lenders undergo annual reviews to maintain their preferred status."}]', 85, 20, 1, true),
('sba-7(a)-module-5-expert', 'SBA 7(a) Portfolio Management', 'quiz', '[{"question":"What is the SBA purchase rate threshold that triggers enhanced monitoring?","options":["5%","10%","15%","20%"],"correct":1,"explanation":"A 10% purchase rate typically triggers enhanced SBA oversight of the lender."},{"question":"Which portfolio metric best predicts future SBA 7(a) default rates?","options":["Average loan size","Delinquency migration rates","Geographic concentration","Industry diversification"],"correct":1,"explanation":"Delinquency migration analysis tracks how loans move through delinquency stages."},{"question":"When should a lender initiate SBA guarantee purchase?","options":["At first missed payment","After 60 days delinquency","When default is likely and liquidation planned","Only after full liquidation"],"correct":2,"explanation":"Purchase should be initiated when default appears likely and liquidation strategy is determined."},{"question":"What portfolio stress test scenario is most relevant for SBA 7(a)?","options":["Interest rate shock","Industry-specific recession","Simultaneous rate and recession","Currency fluctuation"],"correct":2,"explanation":"Combined interest rate and recession scenarios test SBA portfolio resilience most effectively."}]', 85, 20, 1, true),
('sba-7(a)-module-6-expert', 'Advanced SBA 7(a) Underwriting', 'quiz', '[{"question":"What distinguishes SBA 7(a) credit scoring models from conventional models?","options":["They ignore personal credit","They weight business cash flow more heavily","They incorporate SBA-specific risk factors and guarantor analysis","They only use FICO scores"],"correct":2,"explanation":"SBA models incorporate unique factors like SBA guarantee structure and guarantor global analysis."},{"question":"How should an underwriter evaluate a franchise SBA 7(a) loan differently?","options":["No difference from standard","Verify franchise is on SBA Registry and analyze FDD","Only check franchise fee","Require higher down payment"],"correct":1,"explanation":"Franchise loans require SBA Franchise Registry verification and Franchise Disclosure Document analysis."},{"question":"What is the maximum maturity for SBA 7(a) working capital loans?","options":["7 years","10 years","15 years","25 years"],"correct":1,"explanation":"SBA 7(a) working capital loans have a maximum maturity of 10 years."},{"question":"When can an SBA 7(a) lender waive the personal guarantee requirement?","options":["For loans under $25K","When borrower has excellent credit","For owners with less than 20% ownership","Never - all 20%+ owners must guarantee"],"correct":2,"explanation":"Only owners with 20% or more ownership are required to provide personal guarantees."}]', 85, 20, 1, true),
('sba-7(a)-module-7-expert', 'Strategic SBA 7(a) Decision-Making', 'quiz', '[{"question":"What strategic factor most influences SBA 7(a) program profitability for lenders?","options":["Loan volume","Secondary market premium on guaranteed portions","Interest rate spread","Fee income"],"correct":1,"explanation":"Selling guaranteed portions on the secondary market generates significant premium income."},{"question":"How should a lender strategically position its SBA 7(a) program during economic downturns?","options":["Pause all lending","Focus on essential industries with strong cash flow","Reduce guarantee purchases","Increase rates significantly"],"correct":1,"explanation":"Strategic focus on resilient industries maintains portfolio quality during downturns."},{"question":"What emerging SBA 7(a) trend most impacts strategic planning?","options":["Declining loan demand","Fintech lender competition and digital platforms","Reduced SBA budgets","Interest rate caps"],"correct":1,"explanation":"Fintech competition is reshaping SBA lending with faster processing and digital origination."},{"question":"When should a lender consider SBA 7(a) Community Advantage designation?","options":["When serving underserved markets with mission-driven lending","When portfolio exceeds $100M","When seeking higher guarantee percentages","When entering new geographic markets"],"correct":0,"explanation":"Community Advantage targets mission-driven lenders serving underserved communities."}]', 85, 20, 1, true);
INSERT INTO course_assessments (module_id, title, assessment_type, questions, passing_score, time_limit_minutes, order_index, is_required) VALUES
('sba-504-module-2-expert', 'Advanced SBA 504 Project Structuring', 'quiz', '[{"question":"What is the typical SBA 504 financing structure for a standard project?","options":["50% bank, 40% CDC, 10% borrower","60% bank, 30% CDC, 10% borrower","40% bank, 50% CDC, 10% borrower","70% bank, 20% CDC, 10% borrower"],"correct":0,"explanation":"Standard 504 structure: 50% bank first mortgage, 40% CDC/SBA debenture, 10% borrower equity."},{"question":"When does borrower equity requirement increase to 15% in SBA 504?","options":["Loans over $5M","New business (less than 2 years) or special-purpose property","All manufacturing projects","Government contract borrowers"],"correct":1,"explanation":"New businesses or special-purpose properties require 15% equity injection."},{"question":"What is the maximum CDC debenture amount for standard 504 loans?","options":["$2 million","$5 million","$5.5 million","$10 million"],"correct":1,"explanation":"Standard maximum is $5 million; $5.5 million for manufacturing or energy projects."},{"question":"How are 504 debenture interest rates determined?","options":["Prime plus margin","LIBOR-based","Market rate at monthly debenture sale","Lender discretion"],"correct":2,"explanation":"504 rates are set at monthly debenture sales based on current Treasury rates plus spread."}]', 85, 20, 1, true),
('sba-504-module-3-expert', 'Advanced SBA 504 Financial Analysis', 'quiz', '[{"question":"What job creation requirement applies to SBA 504 loans?","options":["1 job per $50K","1 job per $75K","1 job per $90K of CDC debenture","No job requirement"],"correct":2,"explanation":"504 requires creation or retention of 1 job per $90,000 of CDC debenture proceeds."},{"question":"Which financial metric is most critical for 504 project feasibility?","options":["Gross margin","Net operating income and DSCR on combined debt","Working capital ratio","Return on equity"],"correct":1,"explanation":"NOI must support DSCR requirements on both the bank first mortgage and CDC debenture."},{"question":"How should interim financing costs be analyzed in 504 projects?","options":["Ignored in analysis","Included in total project cost and cash flow projections","Deducted from equity","Added to debenture amount"],"correct":1,"explanation":"Interim financing costs must be factored into total project cost and borrower cash flow analysis."},{"question":"What occupancy requirement applies to 504 real estate projects?","options":["25% owner-occupied","51% owner-occupied","75% owner-occupied","100% owner-occupied"],"correct":1,"explanation":"The borrower must occupy at least 51% of an existing building (60% for new construction)."}]', 85, 20, 1, true),
('sba-504-module-4-expert', 'SBA 504 Regulatory & Compliance', 'quiz', '[{"question":"Which entity regulates Certified Development Companies (CDCs)?","options":["Federal Reserve","OCC","SBA Office of Financial Program Operations","FDIC"],"correct":2,"explanation":"SBA OFPO provides direct oversight of CDC operations and compliance."},{"question":"What environmental review standard applies to 504 real estate projects?","options":["Phase I ESA only","NEPA review through SBA","Phase I with potential Phase II per SBA SOP","No environmental review"],"correct":2,"explanation":"504 projects require Phase I ESA with potential Phase II based on findings per SBA SOP."},{"question":"How often must CDCs submit operational reports to SBA?","options":["Monthly","Quarterly","Semi-annually","Annually"],"correct":2,"explanation":"CDCs submit semi-annual reports on portfolio performance and operational metrics."},{"question":"What Davis-Bacon Act requirement applies to 504 construction projects?","options":["Never applies","Applies to all 504 construction","Applies when public benefit test used for job creation waiver","Only for government buildings"],"correct":2,"explanation":"Davis-Bacon prevailing wage requirements apply when the public benefit test substitutes for job creation."}]', 85, 20, 1, true),
('sba-504-module-5-expert', 'SBA 504 Portfolio Management', 'quiz', '[{"question":"What is the CDC responsibility when a 504 loan becomes delinquent?","options":["Transfer to SBA immediately","Perform initial servicing and workout per SBA guidelines","Foreclose immediately","Write off the loan"],"correct":1,"explanation":"CDCs must perform initial servicing, workout negotiations, and reporting per SBA servicing guidelines."},{"question":"How does the 504 secondary market differ from 7(a)?","options":["No secondary market exists","Debentures are pooled and sold as government-backed securities","Individual loan sales only","Only sold to other CDCs"],"correct":1,"explanation":"504 debentures are pooled into government-guaranteed securities sold through monthly sales."},{"question":"What portfolio concentration limit should CDCs monitor?","options":["Industry concentration above 25%","Geographic concentration above 50%","Single borrower exposure above 10%","All of the above"],"correct":3,"explanation":"CDCs should monitor all concentration risks including industry, geographic, and single-borrower exposure."},{"question":"When should a CDC recommend 504 loan restructuring vs. liquidation?","options":["Always restructure first","When present value of restructured payments exceeds liquidation recovery","Only if borrower requests","Never - SBA decides"],"correct":1,"explanation":"Restructuring is preferred when the net present value of restructured cash flows exceeds estimated liquidation recovery."}]', 85, 20, 1, true),
('sba-504-module-6-expert', 'Advanced SBA 504 Underwriting', 'quiz', '[{"question":"What unique appraisal requirement applies to 504 projects?","options":["No appraisal needed","Standard MAI appraisal only","As-completed and as-is value for construction with SBA-approved appraiser","Desktop appraisal sufficient"],"correct":2,"explanation":"504 construction projects require both as-is and as-completed appraisals from SBA-approved appraisers."},{"question":"How should mixed-use properties be underwritten for 504 eligibility?","options":["Based on primary use only","Allocate costs between eligible and ineligible uses","Treat entire property as eligible","Exclude all rental income"],"correct":1,"explanation":"Mixed-use properties require cost allocation to ensure 504 funds only finance eligible business portions."},{"question":"What personal resource test applies to 504 borrowers?","options":["No personal resource test","Net worth under $15M and avg net income under $5M for 2 years","Net worth under $7.5M","Income under $250K"],"correct":1,"explanation":"504 borrowers cannot exceed $15M net worth or $5M average net income over prior 2 years."},{"question":"How should a CDC underwrite a 504 refinance with expansion?","options":["Same as new project","Existing debt must be on acceptable terms and expansion must be at least 10%","Only refinance if property value increased","Require 20% equity minimum"],"correct":1,"explanation":"504 refinance with expansion requires eligible existing debt and expansion costs of at least 10% of total project."}]', 85, 20, 1, true),
('sba-504-module-7-expert', 'Strategic SBA 504 Leadership', 'quiz', '[{"question":"What strategic advantage do CDCs provide over direct SBA lending?","options":["Lower rates only","Local economic development expertise and community-focused lending","Faster processing","Higher loan amounts"],"correct":1,"explanation":"CDCs combine SBA resources with local economic development expertise for community impact."},{"question":"How should CDC leadership evaluate new market expansion?","options":["Enter all available markets","Analyze economic indicators, borrower demand, and competitive landscape","Follow competitor CDCs","Only expand when requested by SBA"],"correct":1,"explanation":"Strategic expansion requires analysis of economic conditions, demand, and existing CDC competition."},{"question":"What emerging 504 program trend most impacts strategic planning?","options":["Declining real estate values","Green energy and sustainability project incentives","Reduced SBA funding","Fewer CDC authorizations"],"correct":1,"explanation":"Green energy incentives and sustainability projects are expanding 504 program opportunities."},{"question":"How should a CDC position itself against conventional commercial real estate lenders?","options":["Compete on rate only","Emphasize below-market fixed rates, longer terms, and lower equity requirements","Avoid competition","Focus only on small loans"],"correct":1,"explanation":"504 advantages include below-market 20-25 year fixed rates and 10% equity vs. conventional 20-30%."}]', 85, 20, 1, true);
INSERT INTO course_assessments (module_id, title, assessment_type, questions, passing_score, time_limit_minutes, order_index, is_required) VALUES
('commercial-real-estate-module-2-expert', 'Complex CRE Deal Structuring', 'quiz', '[{"question":"What is the primary advantage of a participating mortgage in CRE?","options":["Lower initial rate in exchange for equity participation","Faster closing","Simpler documentation","Higher LTV"],"correct":0,"explanation":"Participating mortgages offer lower rates with lender sharing in property appreciation or cash flow."},{"question":"When structuring a CRE mezzanine loan, what intercreditor provision is most critical?","options":["Prepayment penalties","Standstill and cure rights","Insurance requirements","Environmental provisions"],"correct":1,"explanation":"Standstill and cure rights define the mezzanine lenders ability to protect their position during default."},{"question":"What cap rate compression indicates in CRE valuation?","options":["Declining property values","Increasing property values and lower yields","Higher risk","Market instability"],"correct":1,"explanation":"Cap rate compression means investors accept lower yields, driving property values higher."},{"question":"How should a lender structure a CRE construction-to-permanent loan?","options":["Two separate closings","Single close with automatic conversion at CO","Interest-only permanently","Balloon at construction completion"],"correct":1,"explanation":"Single-close construction-to-perm loans automatically convert upon Certificate of Occupancy, reducing costs."}]', 85, 20, 1, true),
('commercial-real-estate-module-3-expert', 'Advanced CRE Financial Analysis', 'quiz', '[{"question":"What is the most accurate method for CRE property valuation?","options":["Comparable sales only","Income capitalization with DCF analysis","Replacement cost","Tax assessment"],"correct":1,"explanation":"Income capitalization combined with DCF analysis provides the most comprehensive CRE valuation."},{"question":"How should a lender stress-test CRE cash flows?","options":["Increase vacancy by 5%","Apply simultaneous vacancy increase, rent reduction, and expense growth","Only test interest rate changes","Use historical averages"],"correct":1,"explanation":"Comprehensive stress testing applies multiple adverse scenarios simultaneously."},{"question":"What NOI adjustment is most commonly overlooked in CRE analysis?","options":["Property taxes","Capital reserves and replacement reserves","Insurance","Management fees"],"correct":1,"explanation":"Capital and replacement reserves are frequently understated or omitted in NOI calculations."},{"question":"When analyzing a CRE portfolio, what correlation risk matters most?","options":["Geographic concentration","Tenant industry concentration","Lease expiration clustering","All of the above"],"correct":3,"explanation":"Portfolio risk requires analysis of geographic, industry, and lease expiration concentrations."}]', 85, 20, 1, true),
('commercial-real-estate-module-4-expert', 'CRE Regulatory & Legal Framework', 'quiz', '[{"question":"What FIRREA requirement impacts CRE loan appraisals?","options":["All loans need appraisals","Appraisals required for federally related transactions over $500K","Only refinances need appraisals","State-licensed appraisers only"],"correct":1,"explanation":"FIRREA requires appraisals by certified appraisers for CRE transactions over $500,000."},{"question":"What environmental liability protection does a CRE lender need?","options":["None if not the owner","Innocent landowner defense through proper due diligence","CERCLA exemption","State environmental insurance"],"correct":1,"explanation":"Lenders must establish innocent landowner defense through adequate environmental due diligence."},{"question":"How does the Dodd-Frank Act impact CRE lending?","options":["No impact","Risk retention requirements for CMBS and stress testing for large lenders","Only affects residential","Caps CRE interest rates"],"correct":1,"explanation":"Dodd-Frank imposes risk retention for CMBS securitization and stress testing for CRE portfolios."},{"question":"What zoning due diligence is most critical in CRE transactions?","options":["Current zoning only","Conforming use verification, variance history, and entitlement status","Building permits","Tax zone classification"],"correct":1,"explanation":"Comprehensive zoning review includes conforming use, variance history, and future entitlement risks."}]', 85, 20, 1, true),
('commercial-real-estate-module-5-expert', 'CRE Portfolio Risk Management', 'quiz', '[{"question":"What is the most effective CRE portfolio diversification strategy?","options":["Geographic spread only","Diversify across property type, geography, tenant, and lease duration","Maximize single property type expertise","Follow market trends"],"correct":1,"explanation":"Multi-dimensional diversification across property type, geography, tenant, and lease reduces concentration risk."},{"question":"How should CRE watch list criteria be established?","options":["DSCR below 1.0x only","Multiple triggers: DSCR decline, vacancy increase, lease rollover, market deterioration","Delinquency only","Annual review only"],"correct":1,"explanation":"Effective watch lists use multiple early warning triggers before loans become delinquent."},{"question":"What CECL methodology is most appropriate for CRE portfolios?","options":["Historical loss rate only","Weighted-average remaining maturity with economic forecasts","Point-in-time model","Regulatory prescribed method"],"correct":1,"explanation":"WARM methodology with forward-looking economic forecasts best captures CRE portfolio expected losses."},{"question":"When should a CRE lender initiate loan restructuring?","options":["At first sign of distress","When restructuring NPV exceeds liquidation value and borrower demonstrates viability","Only if borrower requests","After foreclosure filing"],"correct":1,"explanation":"Restructuring should begin when analysis shows higher recovery than liquidation and borrower viability exists."}]', 85, 20, 1, true),
('commercial-real-estate-module-6-expert', 'Advanced CRE Underwriting', 'quiz', '[{"question":"What underwriting metric best evaluates CRE debt capacity?","options":["LTV ratio","Debt yield (NOI / Loan Amount)","Cap rate","Gross rent multiplier"],"correct":1,"explanation":"Debt yield provides a leverage-neutral measure of loan risk independent of interest rates or amortization."},{"question":"How should a CRE underwriter evaluate tenant credit risk?","options":["Tenant financials only","Tenant financials, industry outlook, lease structure, and replacement cost analysis","Lease term length only","Building occupancy rate"],"correct":1,"explanation":"Comprehensive tenant analysis includes financials, industry trends, lease terms, and replacement market analysis."},{"question":"What is the most important consideration when underwriting a CRE refinance?","options":["Original loan terms","Current property performance, market conditions, and borrower capacity","Historical appreciation","Previous lender relationship"],"correct":1,"explanation":"Refinance underwriting must focus on current property fundamentals, not historical performance."},{"question":"How should environmental risk be quantified in CRE underwriting?","options":["Phase I only","Risk-based scoring incorporating Phase I/II results, property use history, and remediation cost estimates","Ignore if Phase I is clean","Standard deduction from value"],"correct":1,"explanation":"Environmental risk quantification requires comprehensive scoring based on all available data and cost estimates."}]', 85, 20, 1, true),
('commercial-real-estate-module-7-expert', 'Strategic CRE Decision-Making', 'quiz', '[{"question":"What emerging CRE trend most impacts lending strategy?","options":["Declining commercial demand","ESG and sustainability requirements in property valuation","Interest rate stability","Reduced regulation"],"correct":1,"explanation":"ESG factors increasingly impact property values, tenant demand, and regulatory requirements in CRE."},{"question":"How should a CRE lender position for market cycle downturns?","options":["Exit the market","Tighten underwriting standards, build reserves, and focus on essential property types","Increase volume at lower spreads","Reduce staff"],"correct":1,"explanation":"Proactive cycle management includes tighter standards, adequate reserves, and focus on resilient sectors."},{"question":"What technology adoption most improves CRE lending operations?","options":["Social media marketing","Integrated property analytics, automated valuation, and portfolio monitoring platforms","Basic loan origination software","Email communication"],"correct":1,"explanation":"Integrated analytics platforms combining property data, valuation models, and monitoring drive operational efficiency."},{"question":"When should a CRE lender consider CMBS execution vs. portfolio retention?","options":["Always securitize","When portfolio concentration limits approached and market conditions favor securitization","Never securitize","Only for large loans"],"correct":1,"explanation":"CMBS execution should be strategic, used for concentration management when market conditions are favorable."}]', 85, 20, 1, true);
