INSERT INTO course_assessments (module_id, title, assessment_type, questions, passing_score, time_limit_minutes, order_index, is_required) VALUES
('construction-loans-module-2-expert', 'Complex Construction Deal Structuring', 'quiz', '[{"question":"What is the key difference between a construction-to-permanent loan and a standalone construction loan?","options":["Interest rate only","Single close converts automatically vs. two separate transactions","Loan amount limits","Collateral requirements"],"correct":1,"explanation":"Construction-to-perm loans close once and automatically convert, saving time and closing costs."},{"question":"How should a lender structure a phased construction draw schedule?","options":["Equal monthly payments","Based on percentage of completion verified by independent inspection","Front-loaded disbursements","All funds at closing"],"correct":1,"explanation":"Draws should be tied to verified completion percentages with independent third-party inspections."},{"question":"What holdback percentage is standard for construction loan retainage?","options":["5%","10%","15%","20%"],"correct":1,"explanation":"10% retainage is standard, held until substantial completion and lien waiver receipt."},{"question":"When should interest reserves be included in construction loan structuring?","options":["Always","When borrower lacks sufficient cash flow to service interest during construction","Never","Only for residential"],"correct":1,"explanation":"Interest reserves fund debt service during construction when the project generates no income."}]', 85, 20, 1, true),
('construction-loans-module-3-expert', 'Advanced Construction Financial Analysis', 'quiz', '[{"question":"What cost verification method is most reliable for construction budgets?","options":["Contractor estimates only","Independent quantity survey with market-rate cost analysis","Historical project costs","Per-square-foot averages"],"correct":1,"explanation":"Independent quantity surveys verify material quantities and labor at current market rates."},{"question":"How should construction cost overruns be analyzed?","options":["Ignore if under 5%","Evaluate cause, contingency adequacy, and borrower ability to fund shortfall","Automatic loan increase","Stop disbursements"],"correct":1,"explanation":"Overrun analysis must identify root cause, assess remaining contingency, and verify borrower funding capacity."},{"question":"What financial metric best evaluates construction project feasibility?","options":["Simple ROI","Profit margin on total development cost vs. stabilized value","Break-even occupancy","Gross rent multiplier"],"correct":1,"explanation":"Development spread (stabilized value minus total cost) relative to total cost determines project viability."},{"question":"How should soft costs be budgeted in construction analysis?","options":["Fixed 10% of hard costs","Itemized by category with appropriate contingency for each","Excluded from analysis","Included in hard costs"],"correct":1,"explanation":"Soft costs should be individually itemized and budgeted with category-specific contingencies."}]', 85, 20, 1, true),
('construction-loans-module-4-expert', 'Construction Regulatory Framework', 'quiz', '[{"question":"What permit verification is required before construction loan closing?","options":["Building permit only","All required permits: building, environmental, zoning, utility, and access","No permits needed at closing","Only after first draw"],"correct":1,"explanation":"All material permits should be obtained or conditionally approved before construction loan closing."},{"question":"What mechanic lien protection must a construction lender maintain?","options":["Title insurance only","Lien waivers from all contractors/subs with each draw plus title endorsements","Contractor bonds only","No protection needed"],"correct":1,"explanation":"Lien waivers with each disbursement plus title endorsements protect lender priority."},{"question":"How do prevailing wage requirements impact construction loan analysis?","options":["No impact","Increase labor costs 20-40% for applicable projects","Only affect government projects","Reduce project costs"],"correct":1,"explanation":"Prevailing wage requirements on applicable projects can significantly increase labor costs."},{"question":"What OSHA compliance documentation should a construction lender verify?","options":["None","General contractors safety program, insurance certificates, and incident history","Only insurance","Safety training records only"],"correct":1,"explanation":"Comprehensive OSHA compliance review includes safety programs, insurance, and incident history."}]', 85, 20, 1, true),
('construction-loans-module-5-expert', 'Construction Portfolio Management', 'quiz', '[{"question":"What is the most critical construction portfolio risk metric?","options":["Total exposure","Percentage of portfolio in active construction with completion timeline analysis","Average loan size","Geographic spread"],"correct":1,"explanation":"Active construction exposure with completion timeline analysis identifies peak risk periods."},{"question":"How should stalled construction projects be managed?","options":["Immediate foreclosure","Assess completion viability, alternative contractors, and workout options","Wait for borrower action","Extend maturity automatically"],"correct":1,"explanation":"Stalled project management requires completion cost analysis, contractor alternatives, and workout strategy."},{"question":"What early warning indicators predict construction loan problems?","options":["Weather delays only","Draw requests ahead of schedule, change orders, subcontractor disputes, and inspection failures","Borrower financial changes","Interest rate changes"],"correct":1,"explanation":"Multiple operational indicators provide early warning of construction project distress."},{"question":"How should construction loan concentration limits be set?","options":["No limits needed","By property type, geography, contractor, and development stage","Total dollar amount only","Number of loans only"],"correct":1,"explanation":"Construction concentration limits should address property type, location, contractor, and stage diversification."}]', 85, 20, 1, true),
('construction-loans-module-6-expert', 'Advanced Construction Underwriting', 'quiz', '[{"question":"What contractor evaluation factors are most important in construction underwriting?","options":["Years in business only","Financial strength, bonding capacity, project experience, and reference verification","License verification only","Insurance certificates"],"correct":1,"explanation":"Comprehensive contractor evaluation includes financial analysis, bonding, experience, and references."},{"question":"How should a construction lender underwrite speculative development?","options":["Never approve","Apply higher equity requirements, pre-sale thresholds, and enhanced market analysis","Same as pre-sold projects","Require full pre-leasing"],"correct":1,"explanation":"Spec development requires higher equity, pre-sale/lease benchmarks, and rigorous market feasibility analysis."},{"question":"What appraisal methodology is most appropriate for construction loans?","options":["As-is value only","Prospective as-completed and as-stabilized values with discounted cash flow","Comparable sales","Cost approach only"],"correct":1,"explanation":"Construction appraisals need prospective as-completed/stabilized values with DCF for income properties."},{"question":"How should environmental risk be assessed for construction sites?","options":["Phase I only","Phase I plus additional investigation based on prior site use and construction activities","No assessment needed","Phase II always"],"correct":1,"explanation":"Environmental assessment should be risk-based, with additional investigation for brownfield or industrial sites."}]', 85, 20, 1, true),
('construction-loans-module-7-expert', 'Strategic Construction Lending', 'quiz', '[{"question":"What market cycle indicator most impacts construction lending strategy?","options":["Interest rates","Building permit trends and absorption rates relative to pipeline","Stock market performance","Consumer confidence"],"correct":1,"explanation":"Permit trends and absorption rates relative to development pipeline indicate supply-demand balance."},{"question":"How should a construction lender position for market downturns?","options":["Exit construction lending","Tighten to pre-sold/pre-leased projects, increase equity requirements, reduce speculative exposure","Maintain current standards","Focus on larger projects"],"correct":1,"explanation":"Downturn positioning requires tighter standards, higher equity, and reduced speculative construction exposure."},{"question":"What technology best improves construction loan monitoring?","options":["Email updates","Drone surveys, AI progress tracking, and integrated project management platforms","Spreadsheet tracking","Monthly site visits only"],"correct":1,"explanation":"Technology combining aerial monitoring, AI analysis, and project management integration improves oversight efficiency."},{"question":"When should a construction lender consider syndication?","options":["Never","For large projects exceeding concentration limits or risk appetite","All projects over $5M","Only with other banks"],"correct":1,"explanation":"Syndication manages concentration risk and provides capacity for projects exceeding single-lender limits."}]', 85, 20, 1, true);
INSERT INTO course_assessments (module_id, title, assessment_type, questions, passing_score, time_limit_minutes, order_index, is_required) VALUES
('equipment-financing-module-2-expert', 'Complex Equipment Deal Structuring', 'quiz', '[{"question":"What lease structure provides the most tax benefit to the lessee?","options":["Capital lease","Operating lease (true lease)","Sale-leaseback","Direct purchase"],"correct":1,"explanation":"Operating leases keep assets and liabilities off-balance sheet and provide full deduction of lease payments."},{"question":"How should residual value risk be managed in equipment financing?","options":["Ignore residual","Third-party residual guarantees, remarketing agreements, and conservative valuations","Full payout leases only","Insurance coverage"],"correct":1,"explanation":"Residual risk management combines guarantees, remarketing capabilities, and conservative valuation assumptions."},{"question":"What is the advantage of a TRAC lease for commercial vehicles?","options":["Lower payments","Terminal Rental Adjustment Clause allows residual value sharing between lessor and lessee","Tax-free transfer","No maintenance required"],"correct":1,"explanation":"TRAC leases adjust the final payment based on actual vs. projected residual value at lease end."},{"question":"When should a sale-leaseback be recommended over traditional equipment financing?","options":["Always","When borrower needs to free up capital from owned equipment while maintaining use","Only for new equipment","Only for real estate"],"correct":1,"explanation":"Sale-leasebacks convert owned equipment into working capital while retaining operational use."}]', 85, 20, 1, true),
('equipment-financing-module-3-expert', 'Advanced Equipment Financial Analysis', 'quiz', '[{"question":"What depreciation method best reflects equipment economic life?","options":["Straight-line always","Method matching actual usage pattern: units of production, declining balance, or straight-line","MACRS only","No depreciation"],"correct":1,"explanation":"Depreciation should match actual usage patterns, which vary by equipment type and utilization."},{"question":"How should technology obsolescence risk be quantified?","options":["Ignore for short-term loans","Analyze technology lifecycle curves, replacement timing, and impact on equipment utility/value","Standard 20% haircut","Only for computers"],"correct":1,"explanation":"Technology obsolescence requires analysis of innovation cycles, replacement economics, and utility impact."},{"question":"What total cost of ownership factors are most overlooked in equipment analysis?","options":["Purchase price","Installation, training, maintenance, downtime, insurance, and disposal costs","Operating costs","Financing costs"],"correct":1,"explanation":"TCO must include all lifecycle costs beyond purchase: installation, training, maintenance, and disposal."},{"question":"How should a lender evaluate equipment essential-use risk?","options":["Not relevant","Assess whether equipment is critical to borrower operations and revenue generation","Only for medical equipment","Based on replacement cost"],"correct":1,"explanation":"Essential-use equipment supporting core revenue is less likely to be surrendered in financial distress."}]', 85, 20, 1, true),
('equipment-financing-module-4-expert', 'Equipment Regulatory & UCC Framework', 'quiz', '[{"question":"What UCC filing priority rule applies to equipment liens?","options":["First in time","First to file financing statement has priority","Largest creditor priority","Purchase money priority always"],"correct":1,"explanation":"UCC Article 9 generally gives priority to the first creditor to file a financing statement."},{"question":"When does a purchase money security interest (PMSI) have super-priority in equipment?","options":["Always","When properly perfected before or within 20 days of debtor receiving possession","Only for new equipment","Only if notified to other creditors"],"correct":1,"explanation":"Equipment PMSI has super-priority when perfected within 20 days of the debtors possession."},{"question":"What cross-border consideration impacts international equipment financing?","options":["None","Choice of law, Cape Town Convention for aircraft/rail, and export credit agency requirements","Currency only","Tariffs only"],"correct":1,"explanation":"International equipment financing involves multiple legal frameworks including Cape Town Convention registries."},{"question":"How should a lender handle equipment fixtures in UCC filings?","options":["Standard UCC-1 only","Fixture filing in real property records if equipment becomes affixed to real estate","No filing needed","Only for permanent installations"],"correct":1,"explanation":"Equipment becoming fixtures requires fixture filings in real property records to maintain priority."}]', 85, 20, 1, true),
('equipment-financing-module-5-expert', 'Equipment Portfolio Management', 'quiz', '[{"question":"What portfolio metric best predicts equipment finance losses?","options":["Delinquency rate","Vintage analysis combining origination quality, economic conditions, and equipment type performance","Average ticket size","Industry concentration"],"correct":1,"explanation":"Vintage analysis tracking cohorts by origination period reveals patterns in underwriting quality and economic impact."},{"question":"How should equipment remarketing be organized for portfolio recovery?","options":["Auction only","Dedicated remarketing with dealer networks, online platforms, and refurbishment capabilities","Scrap value only","Return to manufacturer"],"correct":1,"explanation":"Effective remarketing combines multiple channels: dealer networks, online auctions, and refurbishment programs."},{"question":"What technology concentration risk should equipment portfolios monitor?","options":["None","Single technology type, manufacturer, or platform concentration that could face obsolescence","Brand diversity only","Age distribution"],"correct":1,"explanation":"Technology concentration creates portfolio-wide obsolescence risk requiring diversification monitoring."},{"question":"How should equipment lease residual positions be stress-tested?","options":["Historical averages","Scenario analysis with technology disruption, market downturn, and oversupply conditions","No stress testing needed","Standard 50% decline"],"correct":1,"explanation":"Residual stress testing must model multiple adverse scenarios including technology and market disruptions."}]', 85, 20, 1, true),
('equipment-financing-module-6-expert', 'Advanced Equipment Underwriting', 'quiz', '[{"question":"What credit scoring approach works best for small-ticket equipment finance?","options":["Manual underwriting","Automated credit scoring with industry-specific risk models","FICO score only","Financial statement analysis"],"correct":1,"explanation":"Small-ticket equipment finance uses automated scoring with industry-specific risk factors for efficiency."},{"question":"How should a lender underwrite startup borrowers for equipment financing?","options":["Automatic decline","Evaluate principals experience, business plan, industry conditions, and equipment essentiality","Same as established businesses","Require 50% down"],"correct":1,"explanation":"Startup equipment underwriting focuses on principal experience, business viability, and equipment criticality."},{"question":"What vendor program risk factors require enhanced underwriting?","options":["None","Vendor financial stability, customer concentration, program volume trends, and residual guarantees","Vendor location only","Product quality only"],"correct":1,"explanation":"Vendor programs require monitoring vendor financial health, concentration, volume, and guarantee capacity."},{"question":"How should hazardous material equipment be underwritten differently?","options":["No difference","Enhanced environmental liability assessment, specialized disposal costs, and regulatory compliance verification","Higher rate only","Shorter term only"],"correct":1,"explanation":"Hazmat equipment requires environmental liability analysis, disposal cost estimation, and regulatory compliance review."}]', 85, 20, 1, true),
('equipment-financing-module-7-expert', 'Strategic Equipment Finance Leadership', 'quiz', '[{"question":"What industry trend most impacts equipment finance strategy?","options":["Declining equipment demand","Equipment-as-a-Service (EaaS) and subscription models disrupting traditional financing","Interest rate changes","Reduced regulation"],"correct":1,"explanation":"EaaS and subscription models are fundamentally changing how businesses acquire and pay for equipment."},{"question":"How should an equipment finance company differentiate in competitive markets?","options":["Lowest rate wins","Industry specialization, speed of execution, flexible structures, and value-added services","Largest loan amounts","Most aggressive residuals"],"correct":1,"explanation":"Differentiation through specialization, speed, flexibility, and advisory services creates sustainable competitive advantage."},{"question":"What strategic acquisition target profile benefits an equipment finance company?","options":["Any profitable company","Complementary industry verticals, geographic expansion, or technology capabilities","Largest competitors","Distressed portfolios"],"correct":1,"explanation":"Strategic acquisitions should add complementary capabilities: new verticals, geographies, or technology."},{"question":"How should ESG considerations impact equipment finance strategy?","options":["Ignore ESG","Develop green finance programs, sustainability-linked pricing, and clean energy equipment focus","Report ESG metrics only","Avoid controversial industries"],"correct":1,"explanation":"ESG strategy includes green finance products, sustainability incentives, and clean energy equipment programs."}]', 85, 20, 1, true);
