
-- Niche beginner tracks modules 2-7 assessments
INSERT INTO course_assessments (module_id, title, assessment_type, questions, passing_score, time_limit_minutes, order_index, is_required) VALUES
-- MCA modules 2-7
('merchant-cash-advances-module-2-beginner', 'MCA Products & Structures', 'quiz', '[{"question":"What is the factor rate in a merchant cash advance?","options":["Interest rate divided by 12","A multiplier applied to the advance amount to determine total repayment","The discount rate on receivables","Annual percentage rate"],"correct":1,"explanation":"Factor rates (e.g., 1.2-1.5) multiply the advance amount to determine total repayment."},{"question":"How are MCA daily payments typically calculated?","options":["Fixed monthly amount","Percentage of daily credit card receipts or bank deposits","Interest-only payments","Balloon payment at maturity"],"correct":1,"explanation":"Daily remittance is a fixed percentage of daily receipts, varying with business revenue."},{"question":"What distinguishes a true MCA from a loan?","options":["Interest rate vs. factor rate","Purchase of future receivables vs. lending with fixed repayment obligation","Term length","Collateral requirements"],"correct":1,"explanation":"A true MCA purchases future receivables at a discount, not a loan with fixed repayment schedule."},{"question":"What is a typical MCA holdback percentage?","options":["5-10%","10-20%","25-50%","50-75%"],"correct":1,"explanation":"Standard holdback rates range from 10-20% of daily receipts."}]', 80, 15, 1, true),
('merchant-cash-advances-module-3-beginner', 'MCA Borrower Assessment', 'quiz', '[{"question":"What minimum monthly revenue typically qualifies for an MCA?","options":["$1,000","$5,000-$10,000","$50,000","$100,000"],"correct":1,"explanation":"Most MCA providers require $5,000-$10,000 minimum monthly revenue."},{"question":"Why is time in business important for MCA qualification?","options":["Legal requirement","Demonstrates stable revenue patterns for repayment estimation","Credit score substitute","Insurance requirement"],"correct":1,"explanation":"Operating history shows revenue consistency needed to predict daily payment capacity."},{"question":"What bank statement analysis is most critical for MCA underwriting?","options":["Average balance","Daily deposit patterns, NSF frequency, and negative balance days","Interest earned","Wire transfer volume"],"correct":1,"explanation":"Daily deposit patterns reveal revenue consistency; NSFs and negative days indicate cash flow stress."},{"question":"How does industry type impact MCA qualification?","options":["No impact","Cash-intensive businesses with consistent daily transactions are preferred","Only restaurants qualify","Manufacturing excluded"],"correct":1,"explanation":"Businesses with steady daily card transactions or deposits are ideal MCA candidates."}]', 80, 15, 1, true),
('merchant-cash-advances-module-4-beginner', 'MCA Documentation & Compliance', 'quiz', '[{"question":"What key document governs an MCA transaction?","options":["Promissory note","Merchant agreement / purchase agreement for future receivables","Mortgage deed","UCC filing"],"correct":1,"explanation":"The merchant agreement defines the purchase of future receivables, holdback percentage, and terms."},{"question":"What state-level regulation increasingly applies to MCAs?","options":["Banking licenses","Commercial financing disclosure requirements","RESPA","Truth in Lending Act"],"correct":1,"explanation":"States like California and New York now require specific disclosures for commercial financing including MCAs."},{"question":"What UCC filing is typically associated with MCAs?","options":["UCC-1 on all assets","UCC-1 on receivables and accounts","No UCC filing needed","UCC-3 amendment"],"correct":1,"explanation":"MCA providers file UCC-1 on receivables/accounts to perfect their interest in purchased receivables."},{"question":"What disclosure must MCA providers give under NY Commercial Finance Disclosure Law?","options":["APR only","Total repayment amount, estimated APR, and payment terms in standardized format","No disclosure required","Interest rate only"],"correct":1,"explanation":"NY law requires standardized disclosure of total cost, estimated APR, and payment terms."}]', 80, 15, 1, true),
('merchant-cash-advances-module-5-beginner', 'MCA Financial Analysis', 'quiz', '[{"question":"How is the effective cost of an MCA calculated?","options":["Factor rate only","Annualized cost based on factor rate and actual repayment period","Prime plus margin","LIBOR spread"],"correct":1,"explanation":"Effective cost annualizes the factor rate based on actual repayment duration."},{"question":"What is a typical effective APR range for merchant cash advances?","options":["5-10%","10-20%","40-150%+","200-500%"],"correct":2,"explanation":"MCAs typically have effective APRs of 40-150%+ due to short repayment periods and factor rates."},{"question":"How should stacking risk be evaluated in MCA analysis?","options":["Not relevant","Assess total daily payment obligations from all advances against daily revenue","Only count current advance","Check credit score"],"correct":1,"explanation":"Stacking multiple MCAs can create unsustainable daily payment obligations exceeding business capacity."},{"question":"What revenue trend analysis matters most for MCA feasibility?","options":["Annual revenue growth","Daily and weekly revenue consistency and seasonal patterns","Monthly totals only","Year-over-year comparison"],"correct":1,"explanation":"Daily/weekly consistency determines whether the business can sustain daily holdback payments."}]', 80, 15, 1, true),
('merchant-cash-advances-module-6-beginner', 'MCA Underwriting Fundamentals', 'quiz', '[{"question":"What is the primary underwriting criterion for MCAs?","options":["Credit score","Consistent daily revenue sufficient to support holdback payments","Collateral value","Net worth"],"correct":1,"explanation":"MCA underwriting focuses on daily revenue consistency and adequacy to support holdback percentage."},{"question":"How should seasonal businesses be underwritten for MCAs?","options":["Decline all seasonal businesses","Adjust advance amount and holdback to account for low-season revenue","Same as non-seasonal","Require personal guarantee"],"correct":1,"explanation":"Seasonal businesses need adjusted advance sizes and holdback rates reflecting low-season cash flow."},{"question":"What risk factor most predicts MCA default?","options":["Industry type","Multiple existing positions (stacking) with high total daily payment burden","Business age","Owner credit score"],"correct":1,"explanation":"Stacking is the strongest predictor of MCA default due to cumulative payment burden."},{"question":"How does personal credit score factor into MCA underwriting?","options":["Primary factor","Secondary factor - minimum threshold with emphasis on business performance","Not considered","Only factor"],"correct":1,"explanation":"Credit score is a threshold qualifier; business revenue performance is the primary underwriting driver."}]', 80, 15, 1, true),
('merchant-cash-advances-module-7-beginner', 'MCA Servicing & Collections', 'quiz', '[{"question":"How are MCA payments typically collected?","options":["Monthly invoice","Automatic daily ACH debit or split-funding from payment processor","Quarterly billing","Annual lump sum"],"correct":1,"explanation":"Daily ACH debits or processor split-funding automatically collect the holdback percentage."},{"question":"What happens when daily MCA payments exceed the agreed holdback percentage?","options":["Nothing","Borrower may dispute; true MCAs should fluctuate with actual revenue","Additional fees apply","Automatic default"],"correct":1,"explanation":"In a true MCA, payments should vary with revenue; fixed daily debits may indicate a loan, not MCA."},{"question":"What workout options exist for struggling MCA borrowers?","options":["None - full repayment required","Restructured holdback percentage, extended term, or consolidated payoff","Immediate foreclosure","Bankruptcy only"],"correct":1,"explanation":"Workout options include reducing holdback rates, extending terms, or consolidating multiple positions."},{"question":"What triggers default in a merchant cash advance?","options":["Missed payment only","Business closure, fraud, breach of agreement, or inability to generate revenue","Credit score decline","Late payments"],"correct":1,"explanation":"MCA defaults typically involve business closure, fraud, or fundamental inability to generate qualifying revenue."}]', 80, 15, 1, true),

-- Franchise modules 2-7
('franchise-financing-module-2-beginner', 'Franchise Financing Products', 'quiz', '[{"question":"What is the most common financing vehicle for franchise acquisitions?","options":["Conventional bank loan","SBA 7(a) loan","Personal savings only","Venture capital"],"correct":1,"explanation":"SBA 7(a) loans are the most widely used for franchise acquisitions due to favorable terms and guarantee."},{"question":"What does the Franchise Disclosure Document (FDD) contain?","options":["Marketing materials","23 items including financial performance, fees, obligations, and franchisor history","Loan application","Business plan template"],"correct":1,"explanation":"The FDD contains 23 required disclosure items providing comprehensive franchise system information."},{"question":"What is Item 19 in the FDD and why is it important for financing?","options":["Territory rights","Financial Performance Representations - provides revenue/profit data for underwriting","Training schedule","Trademark information"],"correct":1,"explanation":"Item 19 provides historical financial performance data critical for projecting borrower cash flow."},{"question":"What franchise fee is typically financed separately?","options":["No fees are financed","Initial franchise fee, which can be included in SBA loan","Royalty fees","Marketing fees"],"correct":1,"explanation":"The initial franchise fee is a one-time cost that can be included in the SBA loan amount."}]', 80, 15, 1, true),
('franchise-financing-module-3-beginner', 'Franchise Borrower Assessment', 'quiz', '[{"question":"What franchisee qualification factor is unique to franchise lending?","options":["Credit score","Franchisor approval and franchise agreement compliance","Net worth","Industry experience"],"correct":1,"explanation":"Franchisees must be approved by the franchisor and comply with franchise agreement requirements."},{"question":"Why is franchisee net worth important beyond standard lending criteria?","options":["Not important","Franchisors typically require minimum net worth and liquid capital thresholds","Only for multi-unit","Insurance requirement"],"correct":1,"explanation":"Franchisors set minimum financial requirements that borrowers must meet independently of loan qualification."},{"question":"How should multi-unit franchise operators be evaluated differently?","options":["Same as single-unit","Assess management capacity, existing unit performance, and development timeline feasibility","Higher credit score needed","Only financial analysis"],"correct":1,"explanation":"Multi-unit operators need management infrastructure, proven unit economics, and realistic development plans."},{"question":"What role does franchise territory play in borrower assessment?","options":["No role","Territory exclusivity, demographics, and market saturation impact revenue projections","Only affects location","Cosmetic factor"],"correct":1,"explanation":"Territory analysis including demographics and competitive saturation directly impacts revenue feasibility."}]', 80, 15, 1, true),
('franchise-financing-module-4-beginner', 'Franchise Documentation & Compliance', 'quiz', '[{"question":"What SBA requirement applies specifically to franchise loans?","options":["No special requirements","Franchise must be listed on SBA Franchise Registry","Higher down payment","Shorter term"],"correct":1,"explanation":"The franchise must appear on the SBA Franchise Registry confirming eligibility for SBA financing."},{"question":"What franchise agreement provision most impacts lender rights?","options":["Marketing requirements","Transfer and assignment restrictions, default/termination provisions","Training schedules","Product sourcing"],"correct":1,"explanation":"Transfer restrictions and termination provisions directly affect lenders ability to recover in default."},{"question":"What is a comfort letter in franchise financing?","options":["Marketing document","Franchisor acknowledgment of lenders interest and agreement to provide notice of default","Borrower reference","Insurance certificate"],"correct":1,"explanation":"Comfort letters protect lenders by ensuring franchisor notification before franchise termination."},{"question":"What ongoing franchise compliance must a lender monitor?","options":["None after closing","Royalty payments current, franchise agreement compliance, and franchisor relationship status","Credit score only","Annual financials only"],"correct":1,"explanation":"Ongoing monitoring includes royalty payment status, compliance, and franchisor relationship health."}]', 80, 15, 1, true),
('franchise-financing-module-5-beginner', 'Franchise Financial Analysis', 'quiz', '[{"question":"What financial metric is most important in franchise analysis?","options":["Gross revenue","Unit-level economics: revenue, COGS, labor, occupancy, and resulting cash flow","Total system sales","Franchise fee ROI"],"correct":1,"explanation":"Unit-level economics determine whether individual franchise locations generate sufficient cash flow."},{"question":"How should royalty fees be treated in franchise cash flow analysis?","options":["Ignore as overhead","Deduct as fixed operating expense before debt service calculation","Include in revenue","Capitalize and amortize"],"correct":1,"explanation":"Royalties are ongoing mandatory expenses that must be deducted before calculating debt service capacity."},{"question":"What is the significance of franchise system-wide average unit volume (AUV)?","options":["Marketing metric only","Benchmark for revenue projections and comparison to borrowers projected performance","Not relevant to lending","Only for mature systems"],"correct":1,"explanation":"AUV provides a benchmark for evaluating whether the borrowers revenue projections are realistic."},{"question":"How should franchise ramp-up period be factored into financial projections?","options":["Assume immediate stabilization","Project 12-18 month ramp with below-average revenue and plan for working capital needs","Not considered","Use system average immediately"],"correct":1,"explanation":"New franchise locations typically take 12-18 months to reach stabilized revenue levels."}]', 80, 15, 1, true),
('franchise-financing-module-6-beginner', 'Franchise Underwriting', 'quiz', '[{"question":"What makes franchise underwriting unique vs. independent business lending?","options":["Lower risk always","Dual analysis: borrower creditworthiness AND franchisor system strength and viability","Same process","Higher standards"],"correct":1,"explanation":"Franchise underwriting evaluates both the individual borrower and the franchise systems health."},{"question":"What franchisor financial health indicator is most important?","options":["Stock price","Franchisee unit growth trends, system-wide sales, and franchisee satisfaction levels","Marketing spend","Number of locations"],"correct":1,"explanation":"Growth trends, system sales, and franchisee satisfaction indicate franchisor system viability."},{"question":"How should franchise renewal risk be assessed?","options":["Ignore if current agreement is long-term","Evaluate renewal terms, renewal rate history, and impact on loan maturity alignment","Check expiration date only","Not relevant to underwriting"],"correct":1,"explanation":"Loan maturity should align with franchise agreement term, and renewal probability should be assessed."},{"question":"What is the impact of franchisor financial distress on franchise lending?","options":["No impact","Can trigger franchise agreement defaults, reduce brand value, and impair borrower revenue","Positive - lower royalties","Only affects franchisor"],"correct":1,"explanation":"Franchisor distress can cascade through the system, impacting franchisee operations and brand value."}]', 80, 15, 1, true),
('franchise-financing-module-7-beginner', 'Franchise Loan Servicing', 'quiz', '[{"question":"What unique servicing event requires immediate attention in franchise lending?","options":["Late payment","Franchise agreement default notice or franchisor termination threat","Annual review","Rate adjustment"],"correct":1,"explanation":"Franchise agreement defaults can lead to termination, eliminating the borrowers ability to operate."},{"question":"How should a franchise lender handle a franchise transfer request?","options":["Approve automatically","Evaluate new franchisee qualification, franchisor approval, and loan assumption terms","Deny all transfers","Not involved in transfers"],"correct":1,"explanation":"Lenders must evaluate transferee qualifications and coordinate with franchisor approval processes."},{"question":"What ongoing reporting should franchise borrowers provide?","options":["None after closing","Periodic financials, royalty payment confirmation, and franchise compliance status","Annual tax returns only","Monthly bank statements"],"correct":1,"explanation":"Franchise loan servicing requires monitoring financials, royalty compliance, and franchise agreement status."},{"question":"When should a franchise lender exercise its comfort letter rights?","options":["Never","When borrower shows distress signs and franchisor termination is possible","Annually","At maturity only"],"correct":1,"explanation":"Comfort letter rights should be exercised proactively when distress indicators suggest potential termination."}]', 80, 15, 1, true),

-- Healthcare modules 2-7
('healthcare-financing-module-2-beginner', 'Healthcare Practice Financing Products', 'quiz', '[{"question":"What is the most common financing structure for medical practice acquisitions?","options":["Equipment lease","SBA 7(a) with practice goodwill and equipment financing","Venture capital","Crowdfunding"],"correct":1,"explanation":"SBA 7(a) loans can finance practice goodwill, equipment, and working capital in a single transaction."},{"question":"Why do healthcare practices typically maintain strong cash flow?","options":["Government subsidies","Recurring patient revenue, insurance reimbursements, and essential-service demand","Low overhead","No competition"],"correct":1,"explanation":"Healthcare benefits from recurring patient visits, insurance payments, and essential-service demand stability."},{"question":"What unique collateral exists in healthcare practice financing?","options":["Real estate only","Patient records/relationships (goodwill), specialized equipment, and accounts receivable","Inventory","Vehicles"],"correct":1,"explanation":"Healthcare practices have unique intangible assets including patient relationships and specialized equipment."},{"question":"How does payer mix impact healthcare financing?","options":["Not relevant","Determines revenue stability: Medicare/Medicaid vs. commercial insurance vs. self-pay ratios","Only affects billing","Cosmetic factor"],"correct":1,"explanation":"Payer mix directly impacts revenue predictability, collection rates, and reimbursement timing."}]', 80, 15, 1, true),
('healthcare-financing-module-3-beginner', 'Healthcare Borrower Assessment', 'quiz', '[{"question":"What unique qualification does a healthcare practice borrower need?","options":["MBA degree","Active professional licenses, board certifications, and clean malpractice history","Real estate ownership","5 years experience minimum"],"correct":1,"explanation":"Healthcare borrowers must maintain valid professional licenses, certifications, and acceptable malpractice records."},{"question":"How should a lenders evaluate healthcare practice goodwill?","options":["Ignore goodwill","Assess patient retention rates, referral sources, provider reputation, and transition plans","Book value only","Revenue multiple only"],"correct":1,"explanation":"Healthcare goodwill evaluation includes patient loyalty, referral networks, reputation, and transition planning."},{"question":"What risk does provider departure pose in healthcare lending?","options":["No risk","Key-person risk if primary provider departs, potentially losing patients and revenue","Only staffing issue","Insurance issue only"],"correct":1,"explanation":"Provider departure can trigger significant patient attrition, directly impacting practice revenue and loan repayment."},{"question":"How should multi-provider practices be evaluated differently?","options":["Same as solo practices","Assess provider mix, employment agreements, non-compete clauses, and succession planning","Higher requirements","Only financial analysis"],"correct":1,"explanation":"Multi-provider analysis includes employment stability, non-competes, revenue distribution, and succession plans."}]', 80, 15, 1, true),
('healthcare-financing-module-4-beginner', 'Healthcare Compliance & Documentation', 'quiz', '[{"question":"What healthcare-specific regulation impacts practice financing?","options":["Sarbanes-Oxley","HIPAA, Stark Law, Anti-Kickback Statute, and state practice act requirements","SEC regulations","OSHA only"],"correct":1,"explanation":"Healthcare practices must comply with HIPAA, Stark Law, Anti-Kickback, and state-specific practice regulations."},{"question":"Why must lenders verify healthcare licensure during loan processing?","options":["Standard requirement","License revocation would immediately terminate practice operations and revenue","Paper trail only","Insurance requirement"],"correct":1,"explanation":"Practice operations depend entirely on valid licenses; revocation eliminates the borrowers ability to generate revenue."},{"question":"What credentialing verification is important in healthcare lending?","options":["Not relevant to lending","Insurance panel participation ensures access to patient volume and reimbursement","Academic credentials only","Only for hospitals"],"correct":1,"explanation":"Insurance panel credentialing determines which patients the practice can serve and bill."},{"question":"How does HIPAA compliance impact healthcare practice due diligence?","options":["No impact on lending","Compliance gaps create regulatory risk, potential fines, and operational disruption","Only affects IT","Patient privacy only"],"correct":1,"explanation":"HIPAA non-compliance can result in significant fines and operational disruption affecting loan repayment."}]', 80, 15, 1, true),
('healthcare-financing-module-5-beginner', 'Healthcare Financial Analysis', 'quiz', '[{"question":"What revenue metric is unique to healthcare financial analysis?","options":["Gross revenue","Collections ratio: actual collections vs. charges, reflecting payer mix effectiveness","Net income","EBITDA"],"correct":1,"explanation":"Collections ratio reveals how effectively the practice converts charges into actual cash collections."},{"question":"How should accounts receivable aging be analyzed in healthcare?","options":["Standard 30-60-90","By payer class aging: Medicare, commercial, self-pay with collection probability by age bracket","Total AR only","Monthly average"],"correct":1,"explanation":"Healthcare AR aging by payer class reveals collection patterns and identifies problematic payer categories."},{"question":"What expense ratio is most important in healthcare practice analysis?","options":["Rent ratio","Staff-to-revenue ratio and provider compensation as percentage of collections","Marketing costs","Supply costs"],"correct":1,"explanation":"Staff costs typically represent 50-65% of collections; provider compensation drives practice profitability."},{"question":"How should reimbursement rate changes impact healthcare financial projections?","options":["Ignore changes","Model projected reimbursement trends by payer and procedure for revenue sensitivity analysis","Assume increases","Use historical rates"],"correct":1,"explanation":"Reimbursement trends by payer and procedure type must be modeled to project future revenue accurately."}]', 80, 15, 1, true),
('healthcare-financing-module-6-beginner', 'Healthcare Underwriting', 'quiz', '[{"question":"What makes healthcare practice underwriting different from general business?","options":["Simpler process","Provider-dependent revenue, regulatory complexity, and reimbursement risk analysis","Same process","Only collateral differences"],"correct":1,"explanation":"Healthcare underwriting must address provider dependency, regulatory compliance, and reimbursement environment."},{"question":"How should malpractice risk be assessed in healthcare underwriting?","options":["Ignore if insured","Review claims history, specialty risk profile, insurance coverage adequacy, and tail coverage","Check insurance certificate only","Not relevant to lending"],"correct":1,"explanation":"Malpractice analysis includes claims history, specialty risk levels, and adequate coverage verification."},{"question":"What technology risk should healthcare lenders evaluate?","options":["None","EHR system adequacy, HIPAA compliance technology, and telehealth capability","Website only","Social media presence"],"correct":1,"explanation":"Technology infrastructure including EHR, HIPAA compliance, and telehealth impacts operational efficiency and compliance."},{"question":"How should a healthcare practice startup be underwritten?","options":["Same as established practice","Evaluate provider credentials, market demand analysis, ramp-up capital, and referral network development","Automatic decline","Require co-signer"],"correct":1,"explanation":"Healthcare startups need provider qualification, market analysis, adequate working capital, and referral development plans."}]', 80, 15, 1, true),
('healthcare-financing-module-7-beginner', 'Healthcare Loan Servicing', 'quiz', '[{"question":"What unique monitoring requirement exists for healthcare practice loans?","options":["Standard monitoring only","License and credentialing status, malpractice insurance, and regulatory compliance verification","Monthly financials only","Annual review"],"correct":1,"explanation":"Healthcare loan servicing must monitor professional licenses, insurance, and regulatory compliance continuously."},{"question":"What event should trigger immediate healthcare loan review?","options":["Late payment only","Provider departure, license issue, major malpractice claim, or payer contract loss","Annual anniversary","Rate change"],"correct":1,"explanation":"Healthcare-specific triggers require immediate assessment of impact on practice viability and loan repayment."},{"question":"How should healthcare industry consolidation impact loan servicing?","options":["No impact","Monitor acquisition offers, competitive changes, and referral network disruptions","Positive always","Only affects large practices"],"correct":1,"explanation":"Industry consolidation can create competitive pressure, acquisition opportunities, or referral disruptions."},{"question":"What succession planning elements should healthcare lenders monitor?","options":["Not relevant","Associate provider development, buy-in agreements, and practice transition timelines","Retirement date only","Estate planning"],"correct":1,"explanation":"Succession planning ensures practice continuity and loan repayment beyond the current providers career."}]', 80, 15, 1, true);
