
-- Restaurant Financing beginner modules 2-7
INSERT INTO course_assessments (module_id, title, assessment_type, questions, passing_score, time_limit_minutes, order_index, is_required) VALUES
('restaurant-financing-module-2-beginner', 'Restaurant Financing Products', 'quiz', '[{"question":"What is the most common SBA loan type for restaurant acquisitions?","options":["SBA Express","SBA 7(a) with equipment and leasehold improvements","SBA 504","Microloan"],"correct":1,"explanation":"SBA 7(a) can finance restaurant acquisitions including equipment, improvements, and working capital."},{"question":"What makes restaurant equipment financing unique?","options":["Standard equipment loans","Specialized restaurant equipment depreciates faster and requires industry-specific valuation","No financing available","Only leases available"],"correct":1,"explanation":"Restaurant equipment has unique depreciation curves and requires specialized appraisal knowledge."},{"question":"Why do restaurants typically need higher working capital reserves?","options":["They dont","High perishable inventory costs, seasonal fluctuations, and thin margins require cash buffers","Regulatory requirement","Insurance costs"],"correct":1,"explanation":"Perishable inventory, seasonal revenue swings, and thin margins (3-9%) demand substantial working capital."},{"question":"What lease consideration is critical in restaurant financing?","options":["Lease length only","Lease term alignment with loan maturity, renewal options, and landlord cooperation","Rent amount only","Location only"],"correct":1,"explanation":"Restaurant loan maturity must align with lease terms, and landlord cooperation affects collateral value."}]', 80, 15, 1, true),
('restaurant-financing-module-3-beginner', 'Restaurant Borrower Assessment', 'quiz', '[{"question":"What unique borrower qualification matters most in restaurant lending?","options":["Credit score","Industry experience, concept viability, and operational management capability","Net worth","Education level"],"correct":1,"explanation":"Restaurant success depends heavily on operator experience, concept-market fit, and management skills."},{"question":"How should a lender evaluate a restaurant concept?","options":["Menu review only","Market analysis, competitive positioning, concept differentiation, and target demographic fit","Decor assessment","Social media presence"],"correct":1,"explanation":"Concept evaluation requires market research, competitive analysis, and demographic alignment."},{"question":"What operational metric best indicates restaurant management quality?","options":["Revenue growth","Food cost percentage, labor cost percentage, and table turnover rates","Customer reviews","Hours of operation"],"correct":1,"explanation":"Operational ratios reveal management effectiveness in controlling costs and maximizing revenue."},{"question":"Why is franchise vs. independent status important in restaurant lending?","options":["No difference","Franchises offer proven systems and brand recognition reducing operational risk","Independent always better","Only affects loan size"],"correct":1,"explanation":"Franchise systems provide operational frameworks and brand value that reduce startup and operational risk."}]', 80, 15, 1, true),
('restaurant-financing-module-4-beginner', 'Restaurant Documentation & Compliance', 'quiz', '[{"question":"What permits must a restaurant borrower provide during loan processing?","options":["Business license only","Health department, liquor license, food handlers, fire safety, and occupancy permits","Building permit only","Tax registration"],"correct":1,"explanation":"Restaurants require multiple permits: health, liquor (if applicable), fire safety, and occupancy."},{"question":"Why is a liquor license particularly important in restaurant financing?","options":["Not important","Liquor sales generate high margins and the license itself has significant transferable value","Legal requirement only","Cosmetic factor"],"correct":1,"explanation":"Liquor licenses contribute significantly to revenue margins and represent valuable collateral."},{"question":"What lease provisions should a restaurant lender verify?","options":["Rent amount only","Assignment rights, exclusivity clauses, CAM charges, and renewal terms","Term length only","Landlord name"],"correct":1,"explanation":"Restaurant lease review must cover assignment rights, exclusivity, common area costs, and renewal options."},{"question":"What insurance requirements are unique to restaurant financing?","options":["Standard business insurance","Liquor liability, food contamination, business interruption, and workers compensation","Property insurance only","General liability only"],"correct":1,"explanation":"Restaurants need specialized coverage for liquor liability, food safety, and business interruption risks."}]', 80, 15, 1, true),
('restaurant-financing-module-5-beginner', 'Restaurant Financial Analysis', 'quiz', '[{"question":"What is a healthy food cost percentage for a full-service restaurant?","options":["15-20%","28-35%","45-50%","60-70%"],"correct":1,"explanation":"Full-service restaurants typically target 28-35% food cost as a percentage of food revenue."},{"question":"What prime cost metric is most important in restaurant analysis?","options":["Rent cost","Combined food and labor costs as percentage of revenue (target: 55-65%)","Marketing spend","Equipment depreciation"],"correct":1,"explanation":"Prime cost (food + labor) should be 55-65% of revenue; higher indicates operational inefficiency."},{"question":"How should restaurant revenue be projected for new locations?","options":["Use national averages","Analyze comparable concepts in similar markets with ramp-up period adjustments","Assume immediate profitability","Use franchisor projections only"],"correct":1,"explanation":"Revenue projections need comparable market analysis with realistic 6-18 month ramp-up assumptions."},{"question":"What seasonality pattern is most common in restaurant financial analysis?","options":["No seasonality","Higher revenue in Q4 holidays and summer; lower in January-February","Consistent year-round","Weekday vs. weekend only"],"correct":1,"explanation":"Most restaurants see peak revenue during holidays and summer months with Q1 dips."}]', 80, 15, 1, true),
('restaurant-financing-module-6-beginner', 'Restaurant Underwriting', 'quiz', '[{"question":"What failure rate statistic is important for restaurant underwriting?","options":["5% failure rate","Approximately 60% of restaurants fail within the first year, 80% within five years","25% failure rate","90% success rate"],"correct":1,"explanation":"High restaurant failure rates require conservative underwriting with emphasis on operator quality."},{"question":"How should restaurant collateral be valued?","options":["Book value","Liquidation value of FF&E, leasehold improvements at depreciated cost, and going-concern value","Replacement cost","Purchase price"],"correct":1,"explanation":"Restaurant collateral includes FF&E at liquidation value, depreciated improvements, and going-concern value."},{"question":"What debt service coverage ratio should restaurants target?","options":["1.0x","1.25x minimum with preference for 1.5x+ given industry volatility","2.0x minimum","0.8x acceptable"],"correct":1,"explanation":"Restaurants need higher DSCR targets (1.25-1.5x) due to revenue volatility and thin margins."},{"question":"How should a lender evaluate restaurant renovation loan requests?","options":["Same as new construction","Assess ROI of improvements, impact on revenue, and minimal business disruption plan","Decline all renovations","Require full closure during renovation"],"correct":1,"explanation":"Renovation analysis must project revenue impact, construction timeline, and business continuity during work."}]', 80, 15, 1, true),
('restaurant-financing-module-7-beginner', 'Restaurant Loan Servicing', 'quiz', '[{"question":"What early warning sign most predicts restaurant loan distress?","options":["Late payment","Declining daily sales trends, increasing food/labor costs, and health inspection issues","Owner absence","Menu changes"],"correct":1,"explanation":"Declining sales combined with rising costs and compliance issues signal financial deterioration."},{"question":"What unique monitoring should restaurant lenders perform?","options":["Annual financial review only","Quarterly sales monitoring, health inspection tracking, and online reputation analysis","Monthly site visits","Credit score checks"],"correct":1,"explanation":"Restaurant monitoring includes sales trends, health compliance, and customer satisfaction indicators."},{"question":"How should a lender handle a restaurant borrowers request for seasonal payment modifications?","options":["Decline all modifications","Evaluate historical seasonal patterns and structure payments aligned with cash flow cycles","Require prepayment","Increase rate"],"correct":1,"explanation":"Seasonal payment structures matching cash flow patterns can prevent unnecessary defaults."},{"question":"What triggers should initiate enhanced restaurant loan monitoring?","options":["Late payment only","Sales decline >10%, health violations, key staff departure, or negative review trends","Annual review date","Rate change"],"correct":1,"explanation":"Multiple operational triggers beyond payment status provide early warning for restaurant loan risk."}]', 80, 15, 1, true),

-- ABL beginner modules 2-7
('asset-based-lending-module-2-beginner', 'ABL Products & Collateral Structures', 'quiz', '[{"question":"What types of collateral are eligible in asset-based lending?","options":["Real estate only","Accounts receivable, inventory, equipment, and intellectual property","Cash only","Personal assets"],"correct":1,"explanation":"ABL utilizes multiple asset classes including receivables, inventory, equipment, and sometimes IP."},{"question":"What is a borrowing base certificate?","options":["Loan application","Periodic calculation of eligible collateral determining maximum borrowing capacity","Insurance document","Financial statement"],"correct":1,"explanation":"The borrowing base certificate calculates eligible collateral values to determine available credit."},{"question":"How does an ABL revolving facility differ from a traditional line of credit?","options":["No difference","Availability fluctuates based on collateral values rather than fixed commitment amount","Lower interest rate","Shorter term"],"correct":1,"explanation":"ABL revolvers tie available credit to verified collateral levels, creating a dynamic borrowing capacity."},{"question":"What is an advance rate in ABL?","options":["Interest rate","Percentage of eligible collateral value that can be borrowed (e.g., 80% on receivables)","Fee structure","Payment schedule"],"correct":1,"explanation":"Advance rates determine borrowing capacity: typically 80-85% on receivables and 50-65% on inventory."}]', 80, 15, 1, true),
('asset-based-lending-module-3-beginner', 'ABL Borrower & Collateral Assessment', 'quiz', '[{"question":"What borrower characteristic makes them ideal for ABL?","options":["High credit score","Asset-rich businesses with strong collateral but limited cash flow or credit history","Profitable companies","Startups"],"correct":1,"explanation":"ABL is designed for businesses with substantial assets that may not qualify for traditional cash flow lending."},{"question":"How should accounts receivable quality be assessed for ABL?","options":["Total AR balance only","Aging analysis, concentration, dilution rates, and customer creditworthiness","Invoice count","Average invoice size"],"correct":1,"explanation":"AR quality assessment includes aging, customer concentration, historical dilution, and debtor credit."},{"question":"What inventory valuation method is most appropriate for ABL?","options":["Book value","Net orderly liquidation value (NOLV) determined by independent appraisal","Replacement cost","FIFO cost"],"correct":1,"explanation":"NOLV represents realistic recovery value if inventory must be liquidated, providing conservative ABL basis."},{"question":"What is collateral dilution and why does it matter in ABL?","options":["Physical deterioration","Reduction in receivable value through credits, returns, and disputes affecting borrowing base","Not relevant","Interest rate adjustment"],"correct":1,"explanation":"Dilution reduces actual collectible AR below face value, directly impacting borrowing base calculations."}]', 80, 15, 1, true),
('asset-based-lending-module-4-beginner', 'ABL Documentation & Field Examination', 'quiz', '[{"question":"What is a field examination in ABL?","options":["Property inspection","On-site audit of borrowers collateral, records, and financial systems by lender representatives","Environmental review","Credit check"],"correct":1,"explanation":"Field exams verify collateral existence, quality, and the accuracy of borrowing base reports."},{"question":"How often are field examinations typically conducted?","options":["Once at closing","Quarterly for new borrowers, semi-annually or annually for established relationships","Monthly","Every 3 years"],"correct":1,"explanation":"Exam frequency depends on risk: quarterly for new/higher-risk, semi-annually for stable borrowers."},{"question":"What key documents govern an ABL facility?","options":["Promissory note only","Loan agreement, security agreement, UCC filings, borrowing base certificate, and intercreditor agreement","Application form","Financial statements"],"correct":1,"explanation":"ABL documentation includes multiple agreements covering lending terms, security interests, and reporting."},{"question":"What does a dominion of funds arrangement mean in ABL?","options":["Borrower controls all funds","Lender controls collection accounts with payments applied to reduce loan balance","Joint account","Escrow arrangement"],"correct":1,"explanation":"Dominion gives the lender control over cash collections, which are applied to reduce the outstanding balance."}]', 80, 15, 1, true),
('asset-based-lending-module-5-beginner', 'ABL Financial & Collateral Analysis', 'quiz', '[{"question":"What financial covenant is most common in ABL facilities?","options":["Debt-to-equity ratio","Minimum fixed charge coverage ratio and borrowing base compliance","Current ratio","Net worth"],"correct":1,"explanation":"FCCR and borrowing base compliance are primary ABL covenants, ensuring repayment capacity and collateral adequacy."},{"question":"How should inventory turnover impact ABL analysis?","options":["Not relevant","Slower turnover reduces inventory advance rates due to increased obsolescence and liquidation risk","Faster is always better","Only affects pricing"],"correct":1,"explanation":"Slow-moving inventory has higher obsolescence risk, warranting lower advance rates in borrowing base."},{"question":"What is the significance of AR aging in ABL analysis?","options":["Billing efficiency only","Receivables over 90 days are typically excluded from eligible collateral","Collection speed","Customer satisfaction"],"correct":1,"explanation":"AR over 90 days is generally deemed ineligible, and cross-aging rules may exclude the entire customer balance."},{"question":"How should seasonal businesses be analyzed for ABL?","options":["Same as non-seasonal","Peak and trough collateral values must be modeled to ensure adequate availability year-round","Decline seasonal borrowers","Only lend during peak"],"correct":1,"explanation":"Seasonal ABL analysis must ensure borrowing base supports needs during both peak and trough periods."}]', 80, 15, 1, true),
('asset-based-lending-module-6-beginner', 'ABL Underwriting Fundamentals', 'quiz', '[{"question":"What is the primary underwriting focus in ABL vs. cash flow lending?","options":["Same focus","Collateral quality, liquidation value, and monitoring capability vs. borrower cash flow","Credit score emphasis","Revenue growth"],"correct":1,"explanation":"ABL underwriting emphasizes collateral quality and recovery potential over traditional cash flow analysis."},{"question":"How should cross-collateralization be structured in ABL?","options":["Never cross-collateralize","Blanket lien on all assets with specific advance rates by collateral category","Only on primary collateral","Separate facilities per asset"],"correct":1,"explanation":"ABL typically uses blanket liens with category-specific advance rates based on each asset types liquidation value."},{"question":"What risk does customer concentration pose in ABL underwriting?","options":["No risk","Single customer representing >25% of AR creates concentration risk if that customer defaults","Only affects large borrowers","Insurance covers this"],"correct":1,"explanation":"High customer concentration means AR loss from one default could significantly impact the borrowing base."},{"question":"How should ABL pricing be determined?","options":["Prime rate only","Base rate plus margin, with facility fees, unused line fees, and field exam charges","Fixed rate","LIBOR spread"],"correct":1,"explanation":"ABL pricing includes interest spread, commitment fees, unused line fees, collateral monitoring, and exam costs."}]', 80, 15, 1, true),
('asset-based-lending-module-7-beginner', 'ABL Monitoring & Workouts', 'quiz', '[{"question":"What is the most critical ongoing ABL monitoring activity?","options":["Annual review","Regular borrowing base verification and collateral audits","Credit score monitoring","Financial statement review"],"correct":1,"explanation":"Continuous borrowing base monitoring and periodic collateral verification are essential ABL oversight activities."},{"question":"What triggers an overadvance in ABL?","options":["Late payment","When outstanding borrowings exceed the calculated borrowing base availability","Rate increase","Covenant breach"],"correct":1,"explanation":"Overadvances occur when collateral values decline below the level supporting outstanding borrowings."},{"question":"How should an ABL lender handle an overadvance situation?","options":["Immediate recall","Evaluate cause, set cure period, adjust advance rates, and monitor repayment plan","Ignore if temporary","Charge penalty fee only"],"correct":1,"explanation":"Overadvance management requires cause analysis, cure timelines, and potentially adjusted advance rates."},{"question":"What ABL workout strategy preserves the most value?","options":["Immediate liquidation","Orderly wind-down with controlled collateral liquidation to maximize recovery","Debt forgiveness","Refinance"],"correct":1,"explanation":"Orderly wind-downs typically achieve higher collateral recovery than forced liquidation through controlled sales."}]', 80, 15, 1, true),

-- Business LOC beginner modules 2-7
('business-lines-of-credit-module-2-beginner', 'LOC Products & Structures', 'quiz', '[{"question":"What is the difference between a committed and uncommitted line of credit?","options":["Interest rate only","Committed lines are legally binding obligations; uncommitted are at lender discretion","Term length","Collateral requirements"],"correct":1,"explanation":"Committed lines create legal lending obligations; uncommitted allow lender to decline individual advances."},{"question":"What is a revolving line of credit?","options":["Fixed-term loan","Credit facility where borrower can draw, repay, and re-draw up to the commitment amount","One-time advance","Equipment financing"],"correct":1,"explanation":"Revolving lines allow repeated borrowing and repayment within the committed amount during the facility term."},{"question":"How does a demand line of credit differ from a term facility?","options":["Interest rate only","Demand lines allow lender to call the full balance at any time; term lines have fixed maturity","Collateral differences","No difference"],"correct":1,"explanation":"Demand facilities give the lender the right to require full repayment at any time."},{"question":"What is a clean-up provision in a line of credit?","options":["Closing requirement","Requirement to reduce balance to zero for a specified period annually","Housekeeping fee","Documentation requirement"],"correct":1,"explanation":"Clean-up provisions (typically 30-60 days at zero balance) ensure the line is used for working capital, not term financing."}]', 80, 15, 1, true),
('business-lines-of-credit-module-3-beginner', 'LOC Borrower Assessment', 'quiz', '[{"question":"What working capital analysis is most important for LOC qualification?","options":["Total assets","Cash conversion cycle analysis: days sales outstanding, inventory days, and days payable","Revenue growth","Profit margin"],"correct":1,"explanation":"The cash conversion cycle reveals how quickly a business converts investments into cash, determining LOC need."},{"question":"What financial ratio best indicates LOC repayment capacity?","options":["Debt-to-equity","Current ratio and quick ratio showing ability to meet short-term obligations","Return on assets","Gross margin"],"correct":1,"explanation":"Liquidity ratios demonstrate the borrowers ability to repay short-term revolving credit obligations."},{"question":"How should seasonal LOC needs be evaluated?","options":["Average monthly use","Peak-to-trough cash flow analysis determining maximum line need and seasonal payment capacity","Annual revenue only","Credit score"],"correct":1,"explanation":"Seasonal analysis identifies maximum borrowing need and the cash flow periods for line repayment."},{"question":"What distinguishes a working capital LOC borrower from a term loan borrower?","options":["Credit quality","Short-term cyclical funding needs vs. long-term capital investment requirements","Loan size","Industry type"],"correct":1,"explanation":"LOC borrowers need short-term cyclical funding; term borrowers need long-term capital for fixed assets."}]', 80, 15, 1, true),
('business-lines-of-credit-module-4-beginner', 'LOC Documentation', 'quiz', '[{"question":"What is the key document in a line of credit facility?","options":["Promissory note only","Credit agreement defining commitment, pricing, covenants, events of default, and borrowing conditions","Application form","Security agreement"],"correct":1,"explanation":"The credit agreement is the master document governing all aspects of the revolving credit facility."},{"question":"What financial reporting covenants are standard in LOC agreements?","options":["Annual tax returns only","Quarterly financials, annual audited statements, borrowing base reports, and compliance certificates","Monthly bank statements","No reporting required"],"correct":1,"explanation":"LOC covenants typically require quarterly financials, annual audits, and ongoing compliance reporting."},{"question":"What is a material adverse change (MAC) clause?","options":["Rate change provision","Provision allowing lender to restrict advances if borrowers financial condition deteriorates significantly","Insurance requirement","Collateral revaluation"],"correct":1,"explanation":"MAC clauses protect lenders by allowing action when significant negative changes occur in borrower condition."},{"question":"What borrowing base documentation is required for secured LOCs?","options":["None","Periodic borrowing base certificates listing eligible collateral and calculating available credit","Annual appraisal","Insurance certificates"],"correct":1,"explanation":"Secured LOCs require regular borrowing base submissions verifying collateral supporting outstanding advances."}]', 80, 15, 1, true),
('business-lines-of-credit-module-5-beginner', 'LOC Financial Analysis', 'quiz', '[{"question":"What financial metric best determines appropriate LOC size?","options":["Annual revenue","Peak working capital deficit based on cash flow cycle analysis","Total assets","Net worth"],"correct":1,"explanation":"LOC size should match the peak working capital gap identified through cash flow cycle analysis."},{"question":"How should LOC utilization patterns be analyzed?","options":["Average balance only","Track utilization rate, peak-to-trough patterns, and compliance with clean-up requirements","Maximum balance","Minimum balance"],"correct":1,"explanation":"Utilization analysis reveals whether the LOC is being used for working capital or disguised term financing."},{"question":"What does persistently high LOC utilization indicate?","options":["Strong business","Potential structural cash flow deficit requiring term financing rather than revolving credit","Good management","Growth phase"],"correct":1,"explanation":"Persistent high utilization suggests the business needs permanent working capital, not revolving credit."},{"question":"How should LOC interest expense be factored into cash flow analysis?","options":["Ignore it","Include estimated interest based on projected utilization levels in cash flow projections","Fixed amount","Use commitment fee only"],"correct":1,"explanation":"Interest projections based on anticipated utilization provide realistic cash flow impact assessment."}]', 80, 15, 1, true),
('business-lines-of-credit-module-6-beginner', 'LOC Underwriting', 'quiz', '[{"question":"What is the primary underwriting question for a line of credit?","options":["Can borrower repay at maturity?","Does the business have genuine short-term working capital needs that will self-liquidate?","Is collateral sufficient?","Is credit score adequate?"],"correct":1,"explanation":"LOC underwriting must confirm genuine short-term needs with self-liquidating repayment sources."},{"question":"How should a lender set LOC covenants?","options":["Standard template for all borrowers","Tailored covenants reflecting borrower-specific risks: leverage, coverage, and liquidity minimums","No covenants needed","Maximum restrictions"],"correct":1,"explanation":"Effective covenants are tailored to borrower risk profile with meaningful financial performance triggers."},{"question":"What pricing components make up total LOC cost?","options":["Interest rate only","Interest rate, commitment fee on unused portion, facility fee, and potential borrowing base monitoring","Fixed fee","Prime rate"],"correct":1,"explanation":"Total LOC cost includes interest on drawn amounts, unused commitment fees, facility fees, and monitoring costs."},{"question":"When should a lender recommend a term loan instead of a LOC?","options":["Never","When funding need is for permanent working capital, fixed assets, or long-term investment","Always for large amounts","When borrower prefers it"],"correct":1,"explanation":"Permanent capital needs should be funded with term debt, not revolving credit facilities."}]', 80, 15, 1, true),
('business-lines-of-credit-module-7-beginner', 'LOC Monitoring & Renewal', 'quiz', '[{"question":"What is the standard renewal period for a business line of credit?","options":["Monthly","Annually with full credit review and updated financials","Every 5 years","No renewal needed"],"correct":1,"explanation":"LOCs are typically renewed annually with comprehensive credit review and updated financial analysis."},{"question":"What utilization pattern should trigger enhanced LOC monitoring?","options":["Low utilization","Consistently high utilization without clean-up, increasing trend, or seasonal pattern deviation","Decreasing utilization","Regular draw-down pattern"],"correct":1,"explanation":"High utilization without clean-up or deviation from expected patterns may indicate deteriorating cash flow."},{"question":"How should LOC renewals be underwritten?","options":["Rubber-stamp if current on payments","Full re-underwriting with updated financials, market conditions, and forward-looking analysis","Credit score check only","Same terms automatically"],"correct":1,"explanation":"Renewals require comprehensive re-evaluation including current financials and forward projections."},{"question":"What event should trigger LOC facility reduction or non-renewal?","options":["Late payment only","Deteriorating financial performance, covenant breaches, industry decline, or misuse of facility","Customer complaint","Market downturn"],"correct":1,"explanation":"Multiple factors including financial deterioration, covenant issues, and facility misuse warrant reduction or non-renewal."}]', 80, 15, 1, true);
