
-- Expert niche track assessments: ABL, Business LOC, MCA, Franchise, Healthcare, Restaurant
-- ABL Expert modules 2-7
INSERT INTO course_assessments (module_id, title, assessment_type, questions, passing_score, time_limit_minutes, order_index, is_required) VALUES
('asset-based-lending-module-2-expert', 'Complex ABL Deal Structuring', 'quiz', '[{"question":"How should a multi-tranche ABL facility be structured?","options":["Single facility for all assets","Separate tranches with distinct advance rates, pricing, and covenants by asset class","Equal allocation","Largest asset class only"],"correct":1,"explanation":"Multi-tranche ABL separates asset classes with tailored terms optimizing borrowing capacity and risk management."},{"question":"What is a springing fixed charge coverage ratio in ABL?","options":["Always tested covenant","Covenant that activates only when excess availability drops below a specified threshold","Variable interest rate","Seasonal adjustment"],"correct":1,"explanation":"Springing covenants activate during stress periods when availability indicates increased default risk."},{"question":"How should cross-border ABL collateral be structured?","options":["Ignore foreign assets","Separate facilities or sub-limits by jurisdiction with local counsel security opinions","Combined under US UCC","Single global filing"],"correct":1,"explanation":"Cross-border ABL requires jurisdiction-specific security structures with local legal opinions."},{"question":"What is the advantage of a first-in-last-out (FILO) tranche in ABL?","options":["Lower cost","Provides incremental borrowing capacity on lower-quality collateral at higher pricing","Faster processing","Better reporting"],"correct":1,"explanation":"FILO tranches unlock additional capacity from collateral that doesnt qualify for standard advance rates."}]', 85, 20, 1, true),
('asset-based-lending-module-3-expert', 'Advanced ABL Collateral Analysis', 'quiz', '[{"question":"How should a lender evaluate intellectual property as ABL collateral?","options":["Not eligible","Assess market value, transferability, remaining useful life, and enforcement costs","Book value only","Ignore IP"],"correct":1,"explanation":"IP collateral evaluation requires market valuation, transferability analysis, and enforcement cost estimation."},{"question":"What advanced inventory analysis technique best supports ABL?","options":["Total inventory count","SKU-level analysis with turnover rates, obsolescence risk, and net orderly liquidation by category","Average value","FIFO vs LIFO comparison"],"correct":1,"explanation":"SKU-level analysis reveals true liquidation value by identifying slow-moving, obsolete, and high-value items."},{"question":"How should receivable dilution trends be interpreted in ABL?","options":["Ignore dilution","Rising dilution indicates declining product quality, pricing disputes, or operational issues requiring reserve adjustments","Normal variation","Customer issue only"],"correct":1,"explanation":"Increasing dilution rates signal underlying business problems requiring higher reserves or lower advance rates."},{"question":"What is the significance of a going-concern vs. liquidation appraisal in ABL?","options":["No difference","Going-concern values operational assets higher; liquidation assumes forced sale - ABL uses liquidation","Marketing difference","Accounting preference"],"correct":1,"explanation":"ABL relies on orderly liquidation values, which are typically 40-70% lower than going-concern values."}]', 85, 20, 1, true),
('asset-based-lending-module-4-expert', 'ABL Regulatory & Legal Framework', 'quiz', '[{"question":"What Article 9 provision most impacts ABL perfection?","options":["Filing location only","Proceeds rules, purchase money priority, and debtor name requirements for UCC filings","Interest rates","Payment terms"],"correct":1,"explanation":"UCC Article 9 perfection requires proper filing location, debtor identification, and understanding priority rules."},{"question":"How does the Bankruptcy Code affect ABL lender rights?","options":["No impact","Adequate protection, automatic stay exceptions, and Section 364 DIP financing provisions","Full protection","Complete loss of rights"],"correct":1,"explanation":"Bankruptcy impacts include adequate protection requirements, stay limitations, and DIP financing opportunities."},{"question":"What intercreditor agreement provisions are critical in split-lien ABL structures?","options":["Payment allocation only","Standstill periods, enforcement rights, lien subordination, and DIP financing consent","Interest rate priority","Reporting sharing"],"correct":1,"explanation":"Intercreditor agreements must address standstill, enforcement, subordination, and DIP financing to prevent creditor conflicts."},{"question":"How should ABL lenders address fraudulent conveyance risk?","options":["Not a concern","Ensure borrower receives reasonably equivalent value and is not rendered insolvent by the transaction","Insurance coverage","Legal disclaimer"],"correct":1,"explanation":"ABL lenders must confirm transactions provide fair value and dont leave borrowers insolvent to avoid voidability."}]', 85, 20, 1, true),
('asset-based-lending-module-5-expert', 'ABL Portfolio Risk Management', 'quiz', '[{"question":"What portfolio stress scenario is most relevant for ABL?","options":["Interest rate shock","Simultaneous receivable quality deterioration, inventory value decline, and liquidity squeeze","Single borrower default","Geographic concentration"],"correct":1,"explanation":"ABL portfolios face correlated risk when economic stress simultaneously impacts receivable quality and inventory values."},{"question":"How should ABL portfolio concentration be managed?","options":["No limits needed","Industry, borrower, collateral type, and geographic diversification with defined limits","Total exposure limits only","Equal allocation"],"correct":1,"explanation":"Multi-dimensional concentration limits prevent portfolio-wide losses from correlated industry or asset class events."},{"question":"What CECL methodology best captures ABL credit risk?","options":["Historical loss rates","Collateral-dependent measurement with forward-looking economic adjustment for each facility","Portfolio average","Standard model"],"correct":1,"explanation":"ABL CECL measurement should be collateral-dependent with facility-level analysis and economic forecasts."},{"question":"How should ABL workout recoveries be tracked and analyzed?","options":["Total recovery only","By collateral type, liquidation method, and time-to-recovery to inform future advance rates","Percentage of balance","Count of resolved loans"],"correct":1,"explanation":"Recovery analysis by asset class and method directly informs advance rate calibration and underwriting standards."}]', 85, 20, 1, true),
('asset-based-lending-module-6-expert', 'Advanced ABL Underwriting & Valuation', 'quiz', '[{"question":"How should a turnaround situation be underwritten for ABL?","options":["Automatic decline","Conservative collateral valuation, enhanced monitoring, higher pricing, and management assessment","Standard underwriting","Lower advance rates only"],"correct":1,"explanation":"Turnaround ABL requires conservative values, frequent monitoring, risk-adjusted pricing, and management evaluation."},{"question":"What synthetic ABL structures are emerging in the market?","options":["None","Hybrid cash flow/ABL facilities with borrowing base toggles based on financial performance","Traditional only","Government programs"],"correct":1,"explanation":"Hybrid facilities combine ABL collateral monitoring with cash flow covenant flexibility based on performance."},{"question":"How should seasonal advance rate adjustments be structured?","options":["Fixed year-round","Pre-approved seasonal overadvances with defined limits, duration, and repayment schedules","No adjustments","Borrower discretion"],"correct":1,"explanation":"Seasonal facilities pre-approve temporary advance rate increases with defined parameters and wind-down schedules."},{"question":"What appraisal methodology innovations improve ABL underwriting?","options":["Standard approaches only","Real-time inventory monitoring, AI-powered receivable analysis, and dynamic valuation models","Blockchain valuations","Self-reported values"],"correct":1,"explanation":"Technology-driven real-time monitoring and AI analytics are enhancing ABL collateral valuation accuracy."}]', 85, 20, 1, true),
('asset-based-lending-module-7-expert', 'Strategic ABL Operations Leadership', 'quiz', '[{"question":"What market trend most impacts ABL strategic positioning?","options":["Declining demand","Middle-market ABL growth driven by alternative lenders and non-bank competition","Interest rate stability","Regulatory reduction"],"correct":1,"explanation":"Non-bank ABL growth and middle-market expansion are reshaping competitive dynamics."},{"question":"How should ABL leadership evaluate technology investments?","options":["Avoid technology","ROI analysis of automated monitoring, digital borrowing base, and portfolio analytics platforms","Adopt everything","Wait for industry standards"],"correct":1,"explanation":"Technology investment decisions should be based on efficiency gains, risk reduction, and competitive positioning."},{"question":"What strategic partnership model benefits ABL operations?","options":["Operate independently","Referral networks with traditional lenders, private equity sponsors, and restructuring advisors","Merger only","Government partnerships"],"correct":1,"explanation":"Strategic partnerships create deal flow from traditional lenders, PE sponsors, and restructuring professionals."},{"question":"How should ABL pricing strategy evolve with market conditions?","options":["Fixed pricing","Dynamic pricing reflecting risk, market competition, facility complexity, and relationship value","Lowest price wins","Cost-plus only"],"correct":1,"explanation":"Strategic pricing balances risk-adjusted returns with competitive positioning and relationship economics."}]', 85, 20, 1, true),

-- Business LOC Expert modules 2-7
('business-lines-of-credit-module-2-expert', 'Complex LOC Deal Structuring', 'quiz', '[{"question":"What is a swing line facility in a syndicated LOC?","options":["Variable rate feature","Short-term sub-facility allowing same-day advances from the agent bank","Seasonal adjustment","Backup facility"],"correct":1,"explanation":"Swing lines provide immediate liquidity within a syndicated facility without waiting for pro-rata funding."},{"question":"How should a multi-currency LOC facility be structured?","options":["USD only","Separate sub-limits by currency with cross-rate conversion mechanisms and hedging requirements","Single currency limit","Borrower choice"],"correct":1,"explanation":"Multi-currency facilities need defined sub-limits, exchange rate mechanisms, and appropriate hedging provisions."},{"question":"What is an accordion feature in a line of credit?","options":["Payment flexibility","Pre-approved expansion option allowing commitment increase without full re-documentation","Interest rate adjustment","Collateral release"],"correct":1,"explanation":"Accordion features enable facility size increases under pre-agreed terms without complete restructuring."},{"question":"How should LOC facilities with letter of credit sub-limits be structured?","options":["Separate facilities","Shared commitment with LC sub-limit, reducing revolving availability by outstanding LCs","No sub-limits","Equal allocation"],"correct":1,"explanation":"LC sub-limits share the total commitment, with outstanding LCs reducing available revolving capacity."}]', 85, 20, 1, true),
('business-lines-of-credit-module-3-expert', 'Advanced LOC Credit Analysis', 'quiz', '[{"question":"What stress testing approach is most effective for LOC portfolios?","options":["Historical averages","Scenario analysis combining revenue decline, margin compression, and working capital elongation","Interest rate shock only","Single factor stress"],"correct":1,"explanation":"Effective LOC stress testing models multiple simultaneous adverse conditions on borrower liquidity."},{"question":"How should operating cycle analysis inform LOC sizing?","options":["Revenue-based formula","Detailed cash conversion cycle modeling with working capital peaks and seasonal adjustments","Fixed percentage of sales","Industry benchmarks only"],"correct":1,"explanation":"Operating cycle analysis identifies precise working capital timing gaps for accurate LOC sizing."},{"question":"What credit migration analysis is important for LOC portfolios?","options":["Rating changes only","Track borrower risk rating movements, utilization trends, and covenant compliance patterns over time","Default count","Delinquency rate"],"correct":1,"explanation":"Migration analysis reveals portfolio health trends through rating movements and behavioral indicators."},{"question":"How should concentration risk be measured in a LOC portfolio?","options":["HHI only","Multi-factor analysis: borrower, industry, geography, facility size, and maturity distribution","Single factor","Total exposure"],"correct":1,"explanation":"Concentration measurement requires multi-dimensional analysis across all relevant risk dimensions."}]', 85, 20, 1, true),
('business-lines-of-credit-module-4-expert', 'LOC Regulatory & Compliance Framework', 'quiz', '[{"question":"What regulatory capital treatment applies to unused LOC commitments?","options":["Zero weight","Credit conversion factor applies, converting unused commitments to risk-weighted assets","Full risk weight","Exempt from capital"],"correct":1,"explanation":"Unused commitments are converted to on-balance-sheet equivalents using credit conversion factors for capital adequacy."},{"question":"How does CECL impact LOC loss reserving?","options":["No impact","Requires lifetime expected credit loss estimation including unfunded commitment exposure","Same as incurred loss","Only funded balances"],"correct":1,"explanation":"CECL requires estimating lifetime losses on both funded and unfunded LOC commitments."},{"question":"What anti-money laundering considerations apply to LOC facilities?","options":["None","KYC on all authorized borrowers, transaction monitoring, and suspicious activity reporting","Standard CDD only","Only for international"],"correct":1,"explanation":"LOC AML compliance includes borrower verification, transaction monitoring, and SAR filing procedures."},{"question":"How do leverage lending guidelines impact LOC underwriting?","options":["Not applicable","Leveraged lending guidance applies when total debt exceeds 6x EBITDA, requiring enhanced underwriting","Only for large banks","Only for term loans"],"correct":1,"explanation":"Leveraged lending thresholds trigger enhanced underwriting, risk management, and reporting requirements."}]', 85, 20, 1, true),
('business-lines-of-credit-module-5-expert', 'LOC Portfolio Risk Management', 'quiz', '[{"question":"What is the most effective LOC portfolio early warning system?","options":["Payment tracking","Behavioral scoring combining utilization changes, overdrafts, deposit trends, and covenant cushion erosion","Annual reviews","Credit bureau monitoring"],"correct":1,"explanation":"Multi-factor behavioral scoring identifies deterioration before financial reporting reveals problems."},{"question":"How should LOC maturity wall risk be managed?","options":["Ignore concentration","Stagger maturities across the portfolio to prevent simultaneous renewal/refinancing pressure","Extend all maturities","Reduce portfolio size"],"correct":1,"explanation":"Maturity distribution prevents concentration of renewal risk in any single period."},{"question":"What portfolio optimization technique improves LOC risk-adjusted returns?","options":["Maximize volume","Risk-based pricing tiers, relationship cross-sell optimization, and covenant calibration","Uniform pricing","Reduce the portfolio"],"correct":1,"explanation":"Portfolio optimization combines risk-adjusted pricing, relationship value, and tailored covenant structures."},{"question":"How should committed but undrawn LOC exposure be stress-tested?","options":["Ignore undrawn","Model drawdown rates under stress scenarios, as borrowers typically fully draw committed lines in crises","50% drawdown assumption","No stress testing needed"],"correct":1,"explanation":"Stress testing must model high drawdown rates on committed facilities during economic downturns."}]', 85, 20, 1, true),
('business-lines-of-credit-module-6-expert', 'Advanced LOC Underwriting Systems', 'quiz', '[{"question":"What automated underwriting model works best for small-business LOC?","options":["Manual only","Credit scorecards combining bureau data, bank data, and financial ratios with policy rules","FICO score only","Revenue-based auto-approval"],"correct":1,"explanation":"Integrated scorecards combining multiple data sources enable efficient, consistent small-business LOC decisions."},{"question":"How should financial covenants be calibrated for different borrower risk profiles?","options":["Same covenants for all","Risk-tiered covenant levels with tighter requirements for higher-risk and looser for investment-grade","No covenants","Maximum restrictions always"],"correct":1,"explanation":"Covenant calibration should reflect borrower risk: tighter for weaker credits, providing headroom for stronger ones."},{"question":"What role does cash flow forecasting play in LOC underwriting?","options":["Not used","13-week cash flow forecasts validate working capital needs and self-liquidating nature of the facility","Annual projections only","Historical analysis only"],"correct":1,"explanation":"Short-term cash flow forecasts verify the LOC will be used for legitimate working capital with clear repayment sources."},{"question":"How should guarantor analysis be conducted for LOC facilities?","options":["PG always required","Risk-based guarantor requirements considering entity strength, ownership structure, and guarantor capacity","Never require PG","Standard net worth test"],"correct":1,"explanation":"Guarantor analysis should be proportional to risk, considering the entitys standalone strength and guarantor resources."}]', 85, 20, 1, true),
('business-lines-of-credit-module-7-expert', 'Strategic Revolving Credit Leadership', 'quiz', '[{"question":"What fintech disruption most impacts traditional LOC lending?","options":["Mobile banking","Real-time cash flow based lending platforms offering instant revolving facilities","Blockchain","Cryptocurrency"],"correct":1,"explanation":"Fintech platforms using real-time bank data and AI are disrupting traditional LOC with instant digital facilities."},{"question":"How should a traditional LOC lender respond to fintech competition?","options":["Ignore fintechs","Invest in digital origination, leverage relationship data, and offer integrated treasury solutions","Lower prices","Exit the market"],"correct":1,"explanation":"Traditional lenders should digitize origination, leverage deep relationships, and bundle treasury services."},{"question":"What ESG consideration is emerging in revolving credit facilities?","options":["None","Sustainability-linked LOC with pricing tied to borrower ESG performance metrics","Green bonds only","Carbon trading"],"correct":1,"explanation":"Sustainability-linked facilities adjust pricing based on borrower achievement of agreed ESG targets."},{"question":"How should LOC portfolio strategy adapt to rising rate environments?","options":["No change needed","Monitor borrower interest coverage, adjust risk ratings, and prepare for increased credit stress","Increase volume","Reduce rates"],"correct":1,"explanation":"Rising rates impact borrower debt service capacity, requiring enhanced monitoring and proactive risk management."}]', 85, 20, 1, true);
