INSERT INTO course_assessments (module_id, title, assessment_type, questions, passing_score, time_limit_minutes, order_index, is_required) VALUES
('sba-express-module-2-expert', 'Advanced Express Loan Structuring', 'quiz', '[{"question":"What is the maximum SBA Express revolving line of credit maturity?","options":["5 years","7 years","10 years","25 years"],"correct":1,"explanation":"SBA Express revolving lines have a maximum maturity of 7 years."},{"question":"How does the SBA Express guarantee percentage differ from standard 7(a)?","options":["Same 85%/75%","50% for all Express loans regardless of size","75% for all Express","No guarantee"],"correct":1,"explanation":"SBA Express loans carry a 50% guarantee regardless of loan amount, compared to 85%/75% for standard 7(a)."},{"question":"What competitive advantage does Express offer lenders vs. standard 7(a)?","options":["Higher guarantee","Delegated authority with 36-hour SBA response using lenders own credit criteria","Lower rates","Longer terms"],"correct":1,"explanation":"Express allows lenders to use their own underwriting procedures with rapid SBA turnaround."},{"question":"When should an Express loan be recommended over standard 7(a)?","options":["Always","For smaller loans where speed is critical and lower guarantee is acceptable to the lender","Never","Only for startups"],"correct":1,"explanation":"Express is ideal when fast processing outweighs the lower guarantee percentage."}]', 85, 20, 1, true),
('sba-express-module-3-expert', 'Advanced Express Credit Analysis', 'quiz', '[{"question":"How should a lender balance Express speed with credit quality?","options":["Skip analysis for speed","Develop streamlined credit scoring models that maintain quality with faster processing","Full manual review always","Automated approval only"],"correct":1,"explanation":"Efficient credit models enable fast decisions without sacrificing underwriting quality."},{"question":"What Express-specific risk factor requires enhanced analysis?","options":["Interest rate risk","Higher lender exposure due to 50% guarantee requiring stronger collateral or cash flow support","Currency risk","Market risk"],"correct":1,"explanation":"The lower 50% guarantee means lenders bear more risk, requiring stronger credit fundamentals."},{"question":"How should Express line of credit utilization be projected?","options":["Assume full utilization","Model seasonal working capital patterns and peak-to-trough borrowing needs","Fixed 50% utilization","No projection needed"],"correct":1,"explanation":"Utilization projections based on working capital cycles ensure appropriate line sizing."},{"question":"What portfolio analytics improve Express lending profitability?","options":["Volume tracking only","Loss-adjusted return analysis comparing Express vs. standard 7(a) by segment","Revenue only","No analytics needed"],"correct":1,"explanation":"Segment-level profitability analysis optimizes the balance between Express speed and standard 7(a) guarantee protection."}]', 85, 20, 1, true),
('sba-express-module-4-expert', 'Express Regulatory & Compliance', 'quiz', '[{"question":"What SBA SOP section governs Express lending authority?","options":["SOP 50 10 general provisions","SOP 50 10 Subpart B Section 3 - SBA Express program-specific requirements","SOP 50 51","No specific SOP section"],"correct":1,"explanation":"Express-specific requirements are detailed in SOP 50 10 with dedicated Express program provisions."},{"question":"What reporting requirement applies to Express loan losses?","options":["No special reporting","Lenders must report Express loss rates separately for SBA program review","Combined with standard 7(a)","Annual summary only"],"correct":1,"explanation":"SBA monitors Express loss rates separately to evaluate lender performance in the delegated program."},{"question":"How does Express delegated authority affect lender compliance obligations?","options":["Reduces compliance burden","Increases lender accountability since SBA relies on lender credit decisions","No change","SBA handles all compliance"],"correct":1,"explanation":"Delegated authority shifts credit decision responsibility to lenders, increasing compliance accountability."},{"question":"What happens if an Express lenders loss rate exceeds SBA thresholds?","options":["Nothing","SBA may restrict or revoke Express authority through program review","Automatic rate increase","Portfolio transfer"],"correct":1,"explanation":"Excessive losses trigger SBA review and potential restriction or revocation of Express lending privileges."}]', 85, 20, 1, true),
('sba-express-module-5-expert', 'Express Portfolio Management', 'quiz', '[{"question":"How should Express loans be segmented for portfolio management?","options":["Single pool","By product (term vs. revolving), industry, geography, and origination vintage","Size only","No segmentation"],"correct":1,"explanation":"Multi-dimensional segmentation enables granular risk monitoring and performance analysis."},{"question":"What Express portfolio metric best predicts program profitability?","options":["Volume","Net yield after losses: interest income minus provision expense and servicing costs","Gross revenue","Number of loans"],"correct":1,"explanation":"Net yield captures the true profitability of Express lending after accounting for higher loss exposure."},{"question":"How should Express revolving line renewals be managed at portfolio level?","options":["Automatic renewal","Risk-tiered renewal process: auto-renew strong credits, full review for deteriorating","Manual review all","Non-renewal default"],"correct":1,"explanation":"Risk-based renewal processes efficiently manage large Express portfolios while maintaining credit quality."},{"question":"What stress scenario most impacts Express portfolio performance?","options":["Rate increase","Small business recession with simultaneous rise in defaults and reduced SBA recovery","Market volatility","Regulatory change"],"correct":1,"explanation":"Small business downturns amplify Express losses due to the 50% guarantee leaving greater lender exposure."}]', 85, 20, 1, true),
('sba-express-module-6-expert', 'Advanced Express Underwriting', 'quiz', '[{"question":"What automated underwriting approach works best for Express?","options":["No automation","Risk-tiered scoring: auto-approve strong credits, streamlined review for moderate, full analysis for weak","Auto-approve all","Manual only"],"correct":1,"explanation":"Tiered automation balances Express speed requirements with appropriate risk assessment intensity."},{"question":"How should Express collateral requirements differ from standard 7(a)?","options":["Same requirements","Lenders follow their own collateral policies, but should compensate for 50% guarantee with stronger coverage","No collateral ever","SBA sets requirements"],"correct":1,"explanation":"Since SBA only guarantees 50%, prudent lenders should seek stronger collateral coverage."},{"question":"What credit scoring threshold optimizes Express lending decisions?","options":["FICO 680+","Risk-adjusted threshold balancing approval rates, loss rates, and guarantee recovery","FICO 720+ only","No minimum"],"correct":1,"explanation":"Optimal thresholds balance volume, credit quality, and the reduced guarantee protection."},{"question":"How should industry risk be factored into Express underwriting?","options":["Ignore industry","Industry risk scores adjusting approval thresholds and pricing for higher-risk sectors","Same for all industries","Decline risky industries"],"correct":1,"explanation":"Industry-adjusted underwriting accounts for sector-specific risk in Express credit decisions."}]', 85, 20, 1, true),
('sba-express-module-7-expert', 'Strategic Express Program Leadership', 'quiz', '[{"question":"What strategic positioning maximizes Express program value?","options":["Volume maximization","Targeted Express lending in niches where speed creates competitive advantage and losses are manageable","Minimize Express use","Replace all with standard 7(a)"],"correct":1,"explanation":"Strategic niche focus leverages Express speed advantages while controlling risk exposure."},{"question":"How should Express complement a broader SBA lending strategy?","options":["Standalone program","Integrated approach: Express for speed-sensitive deals, standard 7(a) for larger or riskier transactions","Replace standard 7(a)","Separate operations"],"correct":1,"explanation":"Express and standard 7(a) serve complementary roles in a comprehensive SBA lending strategy."},{"question":"What technology investment most improves Express program efficiency?","options":["Basic LOS","End-to-end digital platform with automated scoring, e-signatures, and SBA submission integration","Spreadsheet tools","Manual processes"],"correct":1,"explanation":"Fully digital Express platforms maximize the speed advantage while maintaining compliance."},{"question":"How should Express program growth be managed strategically?","options":["Unlimited growth","Controlled growth with loss monitoring, vintage analysis, and capacity planning","Restrict to current volume","Growth at any cost"],"correct":1,"explanation":"Disciplined growth with ongoing performance monitoring ensures sustainable Express program expansion."}]', 85, 20, 1, true);
