INSERT INTO case_studies (module_id, title, company, situation, challenge, solution, outcome, order_index, lessons_learned) VALUES
('sba-7(a)-module-1-expert', 'Complex Multi-Entity Acquisition', 'Heritage Restaurant Group Holdings', 'A private equity-backed group sought to acquire 5 restaurant locations across 3 states simultaneously, requiring $4.2M in SBA 7(a) financing with complex entity structures and inter-company guarantees.', 'The deal involved multiple operating entities, a management company, and a real estate holding company. SBA affiliation rules threatened to disqualify the borrower, and the transaction timeline required coordinated closings across multiple jurisdictions.', 'Structured the transaction with separate SBA 7(a) loans for each operating entity, carefully documenting the absence of affiliation triggers. Negotiated phased closings with escrow arrangements and cross-default provisions that satisfied both the SBA and the sellers.', 'All five acquisitions closed within a 90-day window. Combined revenue exceeded $12M in year one. The group achieved 15% cost savings through centralized purchasing and shared management services, improving DSCR from 1.2x to 1.6x across the portfolio.', 1, '["SBA affiliation rules require careful structuring in multi-entity deals", "Phased closings with escrow arrangements manage complex transaction timing", "Centralized operations create synergies that improve overall debt service", "Cross-jurisdictional deals require coordinated legal counsel in each state"]'::jsonb),
INSERT INTO course_videos (module_id, title, description, video_url, video_type, duration_seconds, order_index, is_active, tags) VALUES
('sba-7(a)-module-1-expert', 'Expert SBA 7(a) Deal Structuring Masterclass', 'Advanced strategies for structuring complex SBA 7(a) transactions including multi-entity deals, partner buyouts, and creative collateral solutions.', 'https://www.youtube.com/watch?v=expert-sba7a-structuring', 'youtube', 2700, 1, true, ARRAY['sba', 'expert', 'deal-structuring']),
('sba-7(a)-module-2-expert', 'Complex Underwriting Case Studies', 'Walk through real-world complex SBA underwriting scenarios with industry-specific adjustments and multi-entity analysis.', 'https://www.youtube.com/watch?v=expert-sba7a-underwriting', 'youtube', 2400, 1, true, ARRAY['sba', 'expert', 'underwriting']),
('commercial-real-estate-module-1-expert', 'CRE Participation & Syndication Structures', 'Deep dive into commercial real estate participation lending, syndication economics, and intercreditor agreements.', 'https://www.youtube.com/watch?v=expert-cre-syndication', 'youtube', 3000, 1, true, ARRAY['cre', 'expert', 'syndication']),
('commercial-real-estate-module-2-expert', 'Advanced CRE Valuation Workshop', 'Hands-on DCF modeling, tenant credit analysis, and environmental risk assessment for commercial properties.', 'https://www.youtube.com/watch?v=expert-cre-valuation', 'youtube', 2700, 1, true, ARRAY['cre', 'expert', 'valuation']),
('construction-loans-module-1-expert', 'Expert Construction Draw Management', 'Advanced draw management systems, cost overrun mitigation, and multi-phase development oversight strategies.', 'https://www.youtube.com/watch?v=expert-construction-draws', 'youtube', 2400, 1, true, ARRAY['construction', 'expert', 'draw-management']),
('equipment-financing-module-1-expert', 'Strategic Equipment Finance Solutions', 'Tax-optimized structures, technology obsolescence management, and sale-leaseback transaction analysis.', 'https://www.youtube.com/watch?v=expert-equipment-strategies', 'youtube', 2100, 1, true, ARRAY['equipment', 'expert', 'tax-optimization']),
('sba-504-module-1-expert', 'SBA 504 Advanced Strategies Workshop', 'Debenture market dynamics, manufacturing provisions, and sophisticated refinancing strategies for the 504 program.', 'https://www.youtube.com/watch?v=expert-sba504-advanced', 'youtube', 2400, 1, true, ARRAY['sba-504', 'expert', 'debenture']),
('working-capital-module-1-expert', 'Working Capital Optimization Masterclass', 'Cash conversion cycle analysis, dynamic borrowing base design, and treasury management integration.', 'https://www.youtube.com/watch?v=expert-working-capital', 'youtube', 2700, 1, true, ARRAY['working-capital', 'expert', 'treasury']),
('invoice-factoring-module-1-expert', 'Advanced Factoring Portfolio Management', 'Account debtor risk stratification, cross-border factoring, and technology-driven factoring operations.', 'https://www.youtube.com/watch?v=expert-factoring-portfolio', 'youtube', 2100, 1, true, ARRAY['factoring', 'expert', 'portfolio']),
('franchise-financing-module-1-expert', 'Multi-Unit Franchise Finance Strategies', 'Area development financing, franchisor analysis, and franchise resale transaction structuring.', 'https://www.youtube.com/watch?v=expert-franchise-multiunit', 'youtube', 2400, 1, true, ARRAY['franchise', 'expert', 'multi-unit']),
('healthcare-financing-module-1-expert', 'Healthcare Practice Valuation & Finance', 'Reimbursement risk assessment, practice valuation methodologies, and regulatory compliance lending.', 'https://www.youtube.com/watch?v=expert-healthcare-valuation', 'youtube', 2700, 1, true, ARRAY['healthcare', 'expert', 'valuation']),
('asset-based-lending-module-1-expert', 'ABL Turnaround Lending Workshop', 'Multi-tranche facility design, DIP financing structures, and field examination best practices.', 'https://www.youtube.com/watch?v=expert-abl-turnaround', 'youtube', 3000, 1, true, ARRAY['abl', 'expert', 'turnaround']);
